	background: -ms-linear-gradient(top, #f0f0f0 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: linear-gradient(to bottom, #f0f0f0 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%);
	cursor: pointer;
}

select:disabled,
select:hover:disabled {
	border: 1px solid #bfbfbf;
	background: #e2e2e2;
	color: #909090;
	background: -moz-linear-gradient(top, #f6f6f6 0%,#e2e2e2 50%,#e2e2e2 51%,#e2e2e2 100%);
