/* View container */
#inventory-container {
	display: -webkit-box;
	display: -moz-box;
	display: -ms-flexbox;
	display: flexbox;

	-webkit-box-flex: 1;
	-moz-box-flex: 1;
	-ms-flex: 1;
	flex: 1;

	-webkit-box-orient: vertical;
	-moz-box-orient: vertical;
	-ms-flex-direction: column;
	flex-direction: column;
    	color: #333e44;
    text-shadow: 2px 2px 5px #fff;
     font-size: 15px !important;
    
    font-weight: 800;
     font-size: 1em !important;
   
    font-weight: bold !important;
    background-color: #268fb7;
background-image: url("https://i.ibb.co/NLLSXdL/header.png");
    background-repeat: no-repeat !important;
	height: 100%;
}

/* Title body flex item */
#inventory-sub-container {
	display: block;
	
	-webkit-box-flex: 1;
	-moz-box-flex: 1;
	-ms-flex: 1;
	flex: 1;

	overflow: auto;

	min-height: 40px;

	margin: 10px;
}

/* Title flex item */
#inventory-title-area-container {
	vertical-align: middle;
	
	-webkit-box-flex: 1;
	-moz-box-flex: 1;
	-ms-flex: 1;
	flex: 1;

	min-height: 40px;
	max-height: 40px;

	margin-bottom: 15px;
}

#inventory-container h2 {
	font-size: 1.2em;
	font-weight: 700;
	color: #a1c1e1;

	margin-left: 5px;
}

#inventory-container .header {
	color: #fff;

	margin-left: 5px;
	margin-top: 5px;
	margin-bottom: 5px;
}

.headerCol, .detailCol {
	min-width: 80px;
	max-width: 80px;
	overflow: hidden;
	margin-left: 5px;
}
}
