html:not(.style-scope)[dark], :not(.style-scope)[dark] {
    --yt-spec-brand-background-solid: #15161C;
    --yt-spec-brand-background-primary: #1E1F29;
    --yt-spec-brand-background-secondary: rgba(40, 40, 40, 0.95);
    --yt-spec-general-background-a: #15161C;
    --yt-spec-general-background-b: #15161C;
    --yt-spec-general-background-c: #6272a4;
    --yt-spec-error-background: #f9f9f9;
    --yt-spec-text-primary: #fff;
    --yt-spec-text-primary-inverse: #030303;
    --yt-spec-text-secondary: #aaa;
    --yt-spec-text-disabled: #717171;
    --yt-spec-call-to-action: #bd93f9;
    --yt-spec-icon-active-other: #fff;
    --yt-spec-icon-inactive: #909090;
    --yt-spec-icon-disabled: #606060;
    --yt-spec-badge-chip-background: rgba(255, 255, 255, 0.1);
    --yt-spec-verified-badge-background: rgba(255, 255, 255, 0.25);
    --yt-spec-suggested-action: #252a3a;
    --yt-spec-button-chip-background-hover: rgba(255, 255, 255, 0.2);
    --yt-spec-touch-response: #fff;
    --yt-spec-paper-tab-ink: rgba(255, 255, 255, 0.3);
    --yt-spec-filled-button-text: #030303;
    --yt-spec-call-to-action-inverse: #065fd4;
    --yt-spec-brand-icon-active: #fff;
    --yt-spec-brand-icon-inactive: #909090;
    --yt-spec-brand-button-background: #ff5555;
    --yt-spec-brand-link-text: #ff5555;
    --yt-spec-filled-button-focus-outline: rgba(255, 255, 255, 0.7);
    --yt-spec-call-to-action-button-focus-outline: rgba(62, 166, 255, 0.3);
    --yt-spec-brand-text-button-focus-outline: rgba(255, 78, 69, 0.3);
    --yt-spec-inactive-text-button-focus-outline: #606060;
    --yt-spec-ad-indicator: #00aaa7;
    --yt-spec-brand-subscribe-button-background: var(--yt-spec-brand-button-background);
    --yt-spec-wordmark-text: #fff;
    --yt-spec-10-percent-layer: rgba(255, 255, 255, 0.1);
    --yt-spec-selected-nav-text: #fff;
    --yt-spec-themed-blue: #673AB7;
    --yt-spec-themed-green: #50fa7b;
    --yt-spec-themed-overlay-background: rgba(0, 0, 0, 0.8);
}

ytd-masthead {
    --paper-input-container-input-align: baseline;
    display: block;
    width: 100%;
    background: var(--yt-spec-brand-background-primary);
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
    opacity: 1;
    background-color: #5f6282;
}
.guide-entry-badge.ytd-guide-entry-renderer {
    color: var(--yt-spec-brand-link-text);
}
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: var(--yt-spec-themed-blue);
}
#notification-count.ytd-notification-topbar-button-renderer {
    background-color: var(--yt-spec-themed-blue);
}
.sbdd_b, .sbsb_a, .sbfl_b, .sbsb_c {
    background: var(--yt-spec-brand-background-primary);
}
.gsfs, .sbfl_b {
    color: #fff;
}
.sbsb_d {
    background-color: var(--yt-spec-general-background-c);
}
.sbdd_b {
    border: 1px solid transparent;
}

@-moz-document url-prefix("https://www.twitch.tv/") {
.chat-line__message {
    animation: fadein 0.6s;
}


@keyframes fadein {
    0% {
        opacity: 0;
        transform: translateY(6rem);
        top: 100px;
    }

    50% {
        transform: translateY(-0.5rem);
    }

    100% {
        opacity: 1;
        transform: translateY(0);
    }
}
}

/* ==UserStyle==
@name           JavaDarks
@namespace      github.com/openstyles/stylus
@version        0.0.1
@description    Dark mode for docs.oracle.com, specifically the javadocs API part of that.
@author         Riedler
@preprocessor   uso
@var    color   bg0     "Background 0"  "#000"
@var	color	bg1		"Background 1"	"#151515"
@var	color	bg2		"Background 2"	"#222"
@var    color   bg3     "Background 3"  "#333"
@var    color   brd     "Border"        "#888"
@var    color   txt     "Text"          "#CCC"
@var    color   htxt    "Bright Text"   "#EEE"
@var    color   bgb     "Blue bg"       "#147"
@var    color   bgo     "Orange bg"     "#C70"
@var    color   lnk     "Links"         "#369"
@var    color   hlnk    "Hovered links" "#6AC"
==/UserStyle== */

@-moz-document
regexp("https:\\/\\/docs\\.oracle\\.com\\/[^\\/(java)]*\\/java\\/javase\\/\\d\\d\\/docs\\/api\\/.*"),
regexp("https?:\\/\\/docs\\.oracle\\.com\\/javase\\/\\d\\/docs\\/api\\/.*") {
    ::-webkit-scrollbar,::-webkit-scrollbar-track-piece{
        background-color:/*[[bg0]]*/ !important;}
    ::-webkit-scrollbar-corner,::-webkit-scrollbar-thumb{
        background-color:/*[[bg3]]*/ !important;
        border-radius:0.4em;}
    :root{
        scrollbar-width:thin;
        scrollbar-color:/*[[bg3]]*/ /*[[bg0]]*/;}
    main,
    header,
    :root{
        background-color:/*[[bg0]]*/;}
    :root{
        font-family:"Lucida Sans Unicode", "Lucida Grande", sans-serif;}
    :root,body,
    span,pre,label,
    .inheritance, .block, .title,
    .contentContainer .description dl dt,
    .contentContainer .details dl dt,
    .serializedFormContainer dl dt{
        color:/*[[txt]]*/;}
    label,
    .navBarCell1Rev{
        user-select: none;
        -ms-user-select: none;
        -moz-user-select: none;
        -khtml-user-select: none;
        -webkit-user-select: none;
        -webkit-touch-callout: none;}
    .topNav{
        background-color:/*[[bgb]]*/;}
    .subNav{
        background-color:/*[[bg2]]*/;}
    .subNavList>li,
    .topNav{
        color:/*[[htxt]]*/}
    a:link,
    a:visited,
    a:link>code,
    a:visited>code{
        color:/*[[lnk]]*/;
        text-decoration:none;}
    a:link:hover,
    a:visited:hover,
    a:link:hover>code,
    a:visited:hover>code{
        color:/*[[hlnk]]*/;}
    table>thead,
    table.striped > thead,
    table.striped>tbody>tr:nth-child(2n),
    table:not(.striped)>tbody>tr,
    table:not(.striped)>tbody>tr>th{
        background-color:/*[[bg1]]*/;}
    table.striped>tbody>tr:nth-child(2n+1),
    thead>tr>th.colFirst,
    thead>tr>th.colSecond,
    thead>tr>th.colLast,
    thead>tr>th.colConstructorName,
    thead>tr>th.colDeprecatedItemName,
    .constantsSummary th,
    .packagesSummary th{
        background-color:/*[[bg2]]*/;}
    .overviewSummary table,
    .memberSummary table,
    .typeSummary table,
    .useSummary table,
    .constantsSummary table,
    .deprecatedSummary table,
    .requiresSummary table,
    .packagesSummary table,
    .providesSummary table,
    .usesSummary table{
        border:none;}
    table>tbody>tr>td[style="background-color:#FFe0d0"]{
        background-color:/*[[bgb]]*/ !important;}
    tbody>tr>*{
        border-top:solid 1px /*[[brd]]*/;}
    .inheritedList,
    section[class$="Details"] .detail{
        background-color:/*[[bg1]]*/;}
    body.class-declaration .summary h3,
    body.class-declaration .details h3,
    body.class-declaration .summary .inheritedList h2{
        background-color:/*[[bg2]]*/;
        border:none;
        border-bottom:solid 1px /*[[brd]]*/;}
    code{
        font-family:monospace;
        border-radius:0.5em;
        color:/*[[htxt]]*/;}
    input[type="text"]{
        background-color:/*[[bg3]]*/;
        color:/*[[txt]]*/;
        border:solid 1px /*[[brd]]*/;}
    input#reset{
        background-color:#0000;}
    .ui-autocomplete-category{
        background-color:/*[[bgb]]*/;}
    .ui-menu{
        overflow-x:hidden;}
    .ui-menu>.ui-menu-item{
        background-color:/*[[bg1]]*/;}
    .ui-menu>.ui-menu-item>.ui-menu-item-wrapper{
        background-image:none !important;
        background-color:/*[[bg1]]*/;
        border-color:#0000;
        color:/*[[txt]]*/;}
    .ui-menu>.ui-menu-item:hover>.ui-menu-item-wrapper{
        background-color:/*[[bg2]]*/ !important;}
    .summary section[class$="Summary"],
    .details section[class$="Details"],
    .classUses .detail, .serializedClassDetails{
        background-color:/*[[bg1]]*/;
        border:none;}
    .overviewSummary caption span,
    .memberSummary caption span,
    .typeSummary caption span,
    .useSummary caption span,
    .constantsSummary caption span,
    .deprecatedSummary caption span,
    .requiresSummary caption span,
    .packagesSummary caption span,
    .providesSummary caption span,
    .usesSummary caption span{
        background-color:/*[[bgb]]*/;}
    .overviewSummary [role="tablist"] .tableTab,
    .memberSummary [role="tablist"] .tableTab,
    .typeSummary [role="tablist"] .tableTab,
    .packagesSummary [role="tablist"] .tableTab{
        background-color:/*[[bgb]]*/;
        color:/*[[txt]]*/;}
    .overviewSummary [role="tablist"] .tableTab:hover,
    .memberSummary [role="tablist"] .tableTab:hover,
    .typeSummary [role="tablist"] .tableTab:hover,
    .packagesSummary [role="tablist"] .tableTab:hover{
        background-color:/*[[bgb]]*/;
        color:/*[[htxt]]*/;}
    .overviewSummary [role="tablist"] .activeTableTab,
    .memberSummary [role="tablist"] .activeTableTab,
    .typeSummary [role="tablist"] .activeTableTab,
    .packagesSummary [role="tablist"] .activeTableTab{
        background-color:/*[[bgo]]*/;
        color:/*[[htxt]]*/;}
    .navBarCell1Rev{
        background-color:/*[[bgo]]*/;
        color:/*[[htxt]]*/;
        font-weight:bold;}
    footer{
        background-color:/*[[bg1]]*/;}
    .bottomNav{
        margin-top:0;
        background-color:/*[[bgb]]*/;}
    .topNav a:link,
    .topNav a:active,
    .topNav a:visited,
    .bottomNav a:link,
    .bottomNav a:active,
    .bottomNav a:visited{
        color:/*[[txt]]*/;}
    .topNav a:link:hover,
    .topNav a:active:hover,
    .topNav a:visited:hover,
    .bottomNav a:link:hover,
    .bottomNav a:active:hover,
    .bottomNav a:visited:hover{
        color:/*[[hlnk]]*/;}
    footer>p.legalCopy{
        margin-bottom:0;
        padding-bottom:1em;}
}

