/* ### POSZUKIWANE NAPISY ### */
@-moz-document regexp("(https|http)://animesub.info/wl/.*") {
    body {
        background: rgb(24, 24, 24) url(data:img/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAAAAACMmsGiAAAABGdBTUEAALGPC/xhBQAAACBjSFJNAAB6JgAAgIQAAPoAAACA6AAAdTAAAOpgAAA6mAAAF3CculE8AAAAAmJLR0QA/4ePzL8AAAAHdElNRQfkAh0JESoeAf5fAAAAHElEQVQI12MU5GVgYBJmYGBg+sXwmYGJgUGYAQATMAI5+D9d8QAAAABJRU5ErkJggg==) repeat !important;
        height: 100%;
        color: #b7b7b7 !important;
    }
    
    #logo {
        border-bottom: #161616 1px solid;
    }
    
    .backLeft {
        border-bottom: #EA1703 6px solid !important;
    }
    
    /* Szkielet - menu */
    #menu, #menuLogin {
        padding: 4px 0;
        background: none !important;
        color: #919191;
        margin-bottom: -10px !important;
    }
    
    #menuLogin input[type="submit"] {
        text-transform: uppercase;
    }
    
    /* Szkielet - zakładki, tytuł */
    #tabs li {
        border: #4e4e4e 1px solid;
        background: #1c1c1c;
    }
    
    #tabs li.active,
    #tabWrap,
    h3.title {
        border: #4e4e4e 1px solid;
        background: #262626;
    }
    
    h3.title {
        padding: 2px 5px 4px 8px !important;
        margin-top: 10px !important;
    }
    
    h3.title span {
        padding-right: 3px !important;
    }
    
    /* Szkielet - Stopka */
    div#foot {
        background: #161616;
        border: #282828 1px solid;
        color: #919191;
    }
    
    #tabs li:hover,
    #tabs li.active:hover {
        background: #2f2f2f;
    }
    
    /* Szkielet - tabela */
    table.list,
    table.list tr th {
        border: 0;
    }
    
    table.list tbody tr:first-of-type th {
        background: #282828;
    }
    
    table.list tr th {
        background: #2e2e2e;
    }
    
    table.list tr.odd td {
        background: #414141;
        border-top: #626262 1px solid;
    }
    
    table.list tr.even td {
        background: #333333;
        border-top: #626262 1px solid;
    }
    
    /* Szkielet - tabela potwierdzenie */
    table.list td.vote div.icon div {
        background: #161616 !important;
        border: #4e4e4e 1px solid !important;
        color: #919191 !important;
        padding: 3px !important;
    }
    
    /* Ogólne */
    hr {
        background-color: #626262;
        height: 1px;
        border: 0;
    }
    
    #pagination a.active {
        color: #919191 !important;
        text-decoration: none !important;
        text-shadow: none !important;
    }
    
    #help, #toDo {
        border: #4e4e4e 1px solid;
        background: #262626;
        padding: 5px 8px;
        margin-top: -8px !important;
    }
}


/* ### Poniżej są elementy wspólne posiadające inne kolory dla każdej kolorystyki ### */
@-moz-document regexp("(https|http)://animesub.info/forum/.*"), regexp("(https|http)://animesub.info/wl/.*") {
    /* Posty - Spoiler, Code i Quote */
    .spoiler_wrap,
    .codebox,
    .quotebox {
        border: #5f5f5f 1px solid !important;
        background: #393939 !important;
    }
    
    .quotebox cite {
