@namespace url(http://www.w3.org/1999/xhtml);
/*
    DeepDark ANSI: Green dla forum i poszukiwanych napisów Animesub.info na licencji "rób sobie z tym co chcesz".
    Wersja 1.3.3
*/

/* ### FORUM ### */
@-moz-document regexp("(https|http)://animesub.info/forum/.*") {
    body {
        background: rgb(24, 24, 24) url(data:img/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAAAAACMmsGiAAAABGdBTUEAALGPC/xhBQAAACBjSFJNAAB6JgAAgIQAAPoAAACA6AAAdTAAAOpgAAA6mAAAF3CculE8AAAAAmJLR0QA/4ePzL8AAAAHdElNRQfkAh0JESoeAf5fAAAAHElEQVQI12MU5GVgYBJmYGBg+sXwmYGJgUGYAQATMAI5+D9d8QAAAABJRU5ErkJggg==) repeat !important;
        height: 100%;
        color: #b7b7b7;
    }
    
    .pun {
        color: #b7b7b7;
    }
    
    /* Szkielet nagłówek */
    .pun #brdheader .box,
    #brdstats .box .inbox {
        background: #161616;
        border: #282828 1px solid;
        color: #919191;
    }
    
    .pun #brdheader .box #brdmenu {
        background: #1c1c1c;
        border: #282828 1px solid;
        border-left: 0 !important;
        border-right: 0 !important;
    }
    
    /* Szkielet stopka */
    .pun #brdfooter .box {
        background: #161616;
        border: #282828 1px solid;
        color: #919191;
    }
    
    .pun #brdfooter .box #modcontrols {
        background: #1c1c1c;
        border-bottom: #282828 1px solid;
    }
    
    /* Szkielet główny */
    .pun #brdmain h2 {
        color: #a0a0a0 !important;
        background: #161616;
        border: #3e3e3e 1px solid;
        border-bottom: 0 !important;
        font-weight: bold;
    }
    
    #punindex #brdmain h2,
    #brdmain #vf h2 {
        background: #1c1c1c !important;
    }
    
    .pun #brdmain .box,
    .pun #brdmain #msg .box,
    #punmoderate #brdmain #msg .box {
        color: #b7b7b7;
        background: #161616;
        border: #3e3e3e 1px solid !important;
    }
    
    /* Szkielet główny - podwójny box */
    .pun #brdmain .box .inform fieldset {
        background: #1c1c1c;
        border: #5f5f5f 1px solid;
    }
    
    /* Szkielet główny - potrójny box - raporty */
    .pun #brdmain .box .inform fieldset table tbody {
        border: #000000 1px solid;
    }
    
    .pun #brdmain .box .inform fieldset table tr th {
        background: #1d1d1d;
        border: 0;
        border-right: #000000 1px solid !important;
    }
    
    .pun #brdmain .box .inform fieldset table tr td {
        background: #232323;
        border: 0;
        border-bottom: #000000 1px solid !important;
    }
    
    /* Szkielet główny - potrójny box - profil */
    .pun #brdmain #viewprofile .box .inform dl dd {
        background: #161616;
        border-left: #282828 1px solid;
        border-right: #282828 1px solid;
        padding-left: 5px;
    }
    
    .pun #brdmain #viewprofile .box .inform dl dd:first-of-type {
        background: #161616;
        border-top: #282828 1px solid;
    }
    
    .pun #brdmain #viewprofile .box .inform dl dd:last-of-type {
        background: #161616;
        border-bottom: #282828 1px solid;
    }

    /* Szkielet główny - widok działów, tematów, wyszukiwań */
    #brdmain div[id*="idx"] h2,
    #brdmain #vf h2 {
        color: #919191 !important;
        background: #161616;
        border: #282828 1px solid !important;
    }
    
    .forumdesc,
    .byuser,
    .newtext,
    .pagestext,
    .polltext,
    .stickytext {
        color: #b7b7b7 !important;
    }
    
    #punindex #brdmain .box,
    #punviewforum #brdmain .box,
    #punsearch #brdmain #vf .box,
    #punmoderate #brdmain #vf .box {
        border: 0 !important;
    }
    
    #brdmain thead tr th {
        background: #2f2f2f;
        border: 0;
    }
    
    #brdmain tbody td {
        border-top: #626262 1px solid;
    }
    
    #brdmain tbody .rowodd .tc2,
    #brdmain tbody .rowodd .tc3,
    #brdmain tbody .rowodd .tcmod,
    #brdmain tbody .roweven .tc2,
    #brdmain tbody .roweven .tc3,
    #brdmain tbody .roweven .tcmod  {
        border: #626262 1px solid;
        border-bottom: 0 !important;
    }
    
    #brdmain tbody .rowodd,
    #brdmain tbody .rowodd .tc2,
    #brdmain tbody .rowodd .tc3,
    #brdmain tbody .rowodd .tcmod {
        background: #414141;
    }
    
    #brdmain tbody .roweven,
    #brdmain tbody .roweven .tc2,
    #brdmain tbody .roweven .tc3,
    #brdmain tbody .roweven .tcmod {
        background: #333333;
    }
    
    #brdmain tbody .rowodd .tcmod,
    #brdmain tbody .roweven .tcmod {
        border-right: 0 !important;
    }
    
     /* Szkielet postów */
     /* Szkielet postów - nagłówek */
    .blockpost h2 {
        background: #222222 !important;
    }
    
    .blockpost h2 .conr {
        color: #919191 !important;
    }
    
     /* Szkielet postów - lewa strona */
    .blockpost .postright,
    .blockpost .postfootright {
        border-left-color: #282828;
    }
    
    .blockpost .postbody .postleft,
    .blockpost .postfoot .postfootleft {
        background: #282828;
        color: #b7b7b7;
    }
    
    /* Szkielet postów - prawa strona */
    .blockpost .box {
        background: #2f2f2f !important;
    }
    
    .blockpost .box .inbox,
    .blockpost .postbody .postright,
    .blockpost .postfoot .postfootright {
        background: #2f2f2f;
        color: #b7b7b7;
    }
    
    .blockpost .postbody .postright hr,
    .postsignature hr {
        background-color: #626262;
    }
    
    /* Szkielet postów - podgląd tematu */
    #punpost #brdmain #postreview h2 {
        background: #1c1c1c !important;
    }
    
    /* Powiadomienia */
    .growlstatus {
        background-color: #161616;
        border: #5f5f5f 1px solid;
        color: #b7b7b7;
    }
    
    /* Posty - insert */
    ins {
        color: #ffffff !important;
        background-color: #5a5a5a !important;
    }
    
    /* Lista użytkowników */
    #users1 h2,
    #users2 h2 {
        background: #1c1c1c;
    }
    
    #users1 thead tr th,
    #users2 thead tr th {
        background: #232323;
    }
    
    #users1 .box,
    #users1 .box .inbox table tbody tr td,
    #users2 .box,
    #users2 .box .inbox table tbody tr td {
        border-bottom: 0px !important;
    }
    
    #users1 tbody tr td,
    #users2 tbody tr td {
        border: #3e3e3e 1px solid;
        border-right: 0 !important;
    }
    
    #users1 tbody tr td.tcr {
        font-size: 11px;
    }
    
    #users1 tbody tr:nth-child(odd),
    #users1 tbody tr:nth-child(odd) .tc2,
    #users1 tbody tr:nth-child(odd) .tc3, 
    #users2 tbody tr:nth-child(odd),
    #users2 tbody tr:nth-child(odd) .tc2,
    #users2 tbody tr:nth-child(odd) .tc3,
    #users2 tbody tr:nth-child(odd) .tc4,
    #users2 tbody tr:nth-child(odd) .tc5 {
        background: #343434;
    }
    
    #users1 tbody tr:nth-child(even),
    #users1 tbody tr:nth-child(even) .tc2,
    #users1 tbody tr:nth-child(even) .tc3,
    #users2 tbody tr:nth-child(even),
    #users2 tbody tr:nth-child(even) .tc2,
    #users2 tbody tr:nth-child(even) .tc3,
    #users2 tbody tr:nth-child(even) .tc4,
    #users2 tbody tr:nth-child(even) .tc5 {
        background: #2f2f2f;
    }
    
    /* Administracja */
    #adstats dl dd {
        background: #1c1c1c;
        border: #3e3e3e 1px solid;
    }
    
    /* Szukaj */
    .multiselect,
    .checklist {
        color: #b7b7b7 !important;
    }
    
    .multiselect .checklist {
        border: #4e4e4e 1px solid !important;
    }
    
    .multiselect .checklist fieldset:last-of-type {
        margin-bottom: 5px;
    }
    
    /* Szukaj z google */
    #punsearch_google #brdmain h2 {
        background: none;
        border: 0;
        padding-left: 2px;
    }
    
    /* Przycisk nowy temat/odpowiedz */
    .pun .postlink.conr a {
        padding: 2px 20px 3px 38px;
        background: #282828 url(data:img/png;base64,) no-repeat;
        background-size: 20px 20px;
        background-position: 8px center;
        border: #4e4e4e 1px solid;
    }
    .pun .postlink a:hover {
        background-color: #2f2f2f;
        text-decoration: none !important;
    }
}


/* ### POSZUKIWANE NAPISY ### */
@-moz-document regexp("(https|http)://animesub.info/wl/.*") {
    body {
        background: rgb(24, 24, 24) url(data:img/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAAAAACMmsGiAAAABGdBTUEAALGPC/xhBQAAACBjSFJNAAB6JgAAgIQAAPoAAACA6AAAdTAAAOpgAAA6mAAAF3CculE8AAAAAmJLR0QA/4ePzL8AAAAHdElNRQfkAh0JESoeAf5fAAAAHElEQVQI12MU5GVgYBJmYGBg+sXwmYGJgUGYAQATMAI5+D9d8QAAAABJRU5ErkJggg==) repeat !important;
        height: 100%;
        color: #b7b7b7 !important;
    }
    
    #logo {
        border-bottom: #161616 1px solid;
    }
    
    .backLeft {
        border-bottom: #EA1703 6px solid !important;
    }
    
    /* Szkielet - menu */
    #menu, #menuLogin {
        padding: 4px 0;
        background: none !important;
        color: #919191;
        margin-bottom: -10px !important;
    }
    
    #menuLogin input[type="submit"] {
        text-transform: uppercase;
    }
    
    /* Szkielet - zakładki, tytuł */
    #tabs li {
        border: #4e4e4e 1px solid;
        background: #1c1c1c;
    }
    
    #tabs li.active,
    #tabWrap,
    h3.title {
        border: #4e4e4e 1px solid;
        background: #262626;
    }
    
    h3.title {
        padding: 2px 5px 4px 8px !important;
        margin-top: 10px !important;
    }
    
    h3.title span {
        padding-right: 3px !important;
    }
    
    /* Szkielet - Stopka */
    div#foot {
        background: #161616;
        border: #282828 1px solid;
        color: #919191;
    }
    
    #tabs li:hover,
    #tabs li.active:hover {
        background: #2f2f2f;
    }
    
    /* Szkielet - tabela */
    table.list,
    table.list tr th {
        border: 0;
    }
    
    table.list tbody tr:first-of-type th {
        background: #282828;
    }
    
    table.list tr th {
        background: #2e2e2e;
    }
    
    table.list tr.odd td {
        background: #414141;
        border-top: #626262 1px solid;
    }
    
    table.list tr.even td {
        background: #333333;
        border-top: #626262 1px solid;
    }
    
    /* Szkielet - tabela potwierdzenie */
    table.list td.vote div.icon div {
        background: #161616 !important;
        border: #4e4e4e 1px solid !important;
        color: #919191 !important;
        padding: 3px !important;
    }
    
    /* Ogólne */
    hr {
        background-color: #626262;
        height: 1px;
        border: 0;
    }
    
    #pagination a.active {
        color: #919191 !important;
        text-decoration: none !important;
        text-shadow: none !important;
    }
    
    #help, #toDo {
        border: #4e4e4e 1px solid;
        background: #262626;
        padding: 5px 8px;
        margin-top: -8px !important;
    }
}


/* ### Poniżej są elementy wspólne posiadające inne kolory dla każdej kolorystyki ### */
@-moz-document regexp("(https|http)://animesub.info/forum/.*"), regexp("(https|http)://animesub.info/wl/.*") {
    /* Posty - Spoiler, Code i Quote */
    .spoiler_wrap,
    .codebox,
    .quotebox {
        border: #5f5f5f 1px solid !important;
        background: #393939 !important;
    }
    
    .quotebox cite {
        color: #E3FF8F !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
    }
    
    .forminfo {
        border: #4a4a4a 1px solid !important;
        background: #1c1c1c !important;
    }
    
    .spoiler_head {
        color: #E3FF8F !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
    }
    
    .spoiler_text {
        color: #b7b7b7;
    }
    
    /* Kwadraty */
    .icon {
        border-color: #ececec #d7d7d7 #cbcbcb #d7d7d7 !important;
    }
	
    .icon-new {
        border-color: #E3FF8F #d7ff61 #cdff3b #d7ff61 !important;
    }
    
    /* Wiadomość */
    div.wrap div#msg {
        background: #161616;
        border: #282828 1px solid;
        color: #919191;
        margin-top: 10px;
        opacity: 1 !important;
    }
    
    div#msg div.msg {
        background: #161616 !important;
        color: #E3FF8F;
    }
    
    /* Linki widok tematów - brak nowych postów */
    #vf a:link,
    #vf a:visited,
    #vf a:hover {
        color: #EBFFB0 !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
    }
    
    /* Linki widok tematów - nowe posty */
    #vf [class*="inew"] a:link,
    #vf [class*="inew"] a:visited,
    #vf [class*="inew"] a:hover {
        color: #E3FF8F !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
    }
    
    /* Linki */
    a:link,
    a:visited,
    a:link strong,
    a:visited strong,
    a:link img,
    a:visited img,
    a:link em,
    a:visited em,
    a:link .bbu,
    a:visited .bbu,
    a:link .bbs,
    a:visited .bbs,
    a:link span,
    a:visited span,
    #feedBody a:link,
    #feedBody a:visited,
    legend {
        color: #E3FF8F !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
        text-decoration: none;
    }
    
    a:hover,
    a strong:hover,
    a img:hover,
    a em:hover,
    a .bbu:hover,
    a .bbs:hover,
    a span:hover,
    #feedBody a:hover {
        color: #EBFFB0 !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
        text-decoration: underline!important;
    }
    
    /* Ankieta */
    .poll {
        background: #1c1c1c !important;
        border: #5f5f5f 1px solid !important;
    }
    
    .poll li label {
        background: #1c1c1c !important;
    }
    
    .poll ol li p.progressbar {
	   border: 1px solid #4F6131 !important;
	   border: 1px solid rgba(227,255,145,0.2) !important;
    }
    
    .poll ol li p.progressbar span {
        background-color: rgba(106,222,28, 0.2);
        box-shadow: 0 0 10px rgba(106,222,28, 0.2) inset !important;
    }
    
    .poll .total {
        color: #919191;
    }
    
    /* RSS */
    #feedHandler {
        background: rgb(24, 24, 24) url(data:img/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAAAAACMmsGiAAAABGdBTUEAALGPC/xhBQAAACBjSFJNAAB6JgAAgIQAAPoAAACA6AAAdTAAAOpgAAA6mAAAF3CculE8AAAAAmJLR0QA/4ePzL8AAAAHdElNRQfkAh0JESoeAf5fAAAAHElEQVQI12MU5GVgYBJmYGBg+sXwmYGJgUGYAQATMAI5+D9d8QAAAABJRU5ErkJggg==) repeat !important;
    }
    
    #feedHeaderContainer, #feedHeaderContainer #feedHeader, #feedBody {
        background-color: #161616 !important;
        color: #919191 !important;
    }
    
    #feedHeaderContainer, #feedBody {
        border: #3e3e3e 1px solid !important;
    }
    
    #feedBody #feedTitleText {
        color: #E3FF8F !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
        border-color: #E3FF8F;
    }
    
    #feedBody .spoiler_head,
    #feedBody .spoiler_text {
        padding: 3px 0 5px 5px !important;;
    }
    
    #feedBody .spoiler_wrap {
        max-block-size: 400px !important;
        overflow: hidden !important;
    }
    
    #feedBody .spoiler_wrap {
        overflow: auto !important;
    }
    
    #feedBody .quotebox,
    #feedBody .spoiler_wrap,
    #feedBody .codebox {
        background-color: #282828 !important;
    }
    
    #feedBody cite {
        padding: 3px 0 5px 5px;
    }
    
    /* Przyciski, pola tekstowe, pola rozwijane */
    form button,
    input[type="button"],
    input[type="submit"],
    input[type="file"],
    .multidelete label {
        color: #E3FF8F !important;
        text-shadow: 0 0 5px rgba(106,222,28, 0.4) !important;
        background: #282828 !important;
        border: 1px solid #4e4e4e !important;
        padding: 3px 8px;
    }
    
    .multidelete label {
        padding-bottom: 6px !important;
    }
    
    form button:hover,
    input[type="button"]:hover,
    input[type="submit"]:hover,
    input[type="file"]:hover,
    .multidelete label:hover {
        background: #2f2f2f !important;
    }
    
    input.longinput,
    textarea,
    input[type="text"],
    input[type="password"] {
        border: 1px solid #242426 !important;
        border: 1px solid rgba(255, 255, 255, 0.15) !important;
        background: #000000 !important;
        background: rgba(0, 0, 0, 0.6) !important;
        box-shadow: 0 0 8px rgba(255, 255, 255, 0.04) !important;
        color: #565658 !important;
    }
    
    input.longinput:focus,
    textarea:focus,
    input[type="text"]:focus,
    input[type="password"]:focus {
        outline: none !important;
        color: #797979 !important;
        border-color: #242426 !important;
        border-color: rgba(255, 255, 255, 0.15) !important;
        background: rgba(0, 0, 0, 0.7) !important;
        box-shadow: inset 0 0 5px rgba(255, 255, 255, 0.08) !important;
    }
    
    input.longinput:hover,
    textarea:hover,
    input[type="text"]:hover,
    input[type="password"]:hover {
        -webkit-transition: border-color 0.25s ease-in-out, color 0.25s ease-in-out, background 0.25s ease-in-out;
        -moz-transition: border-color 0.25s ease-in-out, color 0.25s ease-in-out, background 0.25s ease-in-out;
        -o-transition: border-color 0.25s ease-in-out, color 0.25s ease-in-out, background 0.25s ease-in-out;
        -ms-transition: border-color 0.25s ease-in-out, color 0.25s ease-in-out, background 0.25s ease-in-out;
        transition: border-color 0.25s ease-in-out, color 0.25s ease-in-out, background 0.25s ease-in-out;
    }
    
    select {
        background-color: #1c1c1c;
        border: #5f5f5f 1px solid;
        color: #919191;
    }
    
    select optgroup {
        background-color: #1c1c1c;
        border: #5f5f5f 0px solid;
        color: #b7b7b7;
    }
    
    select optgroup option {
        background-color: #1c1c1c;
        border: #5f5f5f 0px solid;
        color: #919191;
    }
}

@namespace url(http://www.w3.org/1999/xhtml);
