    border-radius: 10px;
}

.their-message:after {
    content: "";
    position: absolute;
    box-shadow: rgba(100, 100, 100, 0.3) 2px 2px 2px;
    -moz-transform: rotate(135deg);
    -webkit-transform: rotate(135deg);
    left: -7px;
