    /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, #bdd9f5 0%, #96bade 100%);
    /* Opera 11.10+ */
    background: -ms-linear-gradient(top, #bdd9f5 0%, #96bade 100%);
    /* IE10+ */
    background: linear-gradient(to bottom, #4395fe 0%, #4395fe 100%);
    /* W3C */
    
    
    
    float: right;
    clear: both;
    max-width: 60%;
    height: auto;
    margin: 20px 30px;
