@-moz-document domain("hardware.info") {
/* article alinea spacing */
.article__content blockquote, .article__content p, .article__content table:not(.product-table){
    margin-bottom: 16px;   
}

/* article other news */
/*.sitearea_content > .related.sidebar-item,*/
.sitearea_content> .main > .related.sidebar-item{
    display: none;
}

/* comments header */
.comments_header{
    padding: 0px;
}
/* comment */
div[id^=reactie-]{
    margin: 15px 0;
}
.fos_comment_comment_reply{
   /* display: none;*/
    padding-top: 0px;
    padding-bottom: 15px;
}
.fos_comment_submit{
    padding-bottom: 0px;
}

/* title */
h1{
    margin: 0px;
}

/* lower min height of textarea */
.editor+textarea{
    min-height: 200px;
}

/* ad fix */
body > div[style*="display: block !important;"]{
    margin-bottom: 30px !important;    
}

/* autor article */
.article__data{
    margin: 20px 0;
}

.article__content__social-bar{
    margin-top: 12px;
}

.article__ruler{
    margin: 12px 0;
}

.overview-item--large{
    padding: 18px 0;
}

.pagination__container{
    padding:18px 0;
    margin-bottom: 18px;
}

.sitearea_content {
    margin-top: -15px;
}

.showcase-item--small .showcase-item__image,
.showcase-item--small .showcase-item__image-container{
    margin-right: 15px;
}
}

