@-moz-document domain("quora.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("quora.com") {
/*__________________________________________________________________________________*/

a>.ui_content_title:hover,
* {
	text-decoration: none!important;
}

selector {
	text-transform: capitalize!important;
}

*,
.Carousel .left,
.Carousel .right,
.CondensedTribesCarousel .CarouselItem,
.CondensedTribesCarousel .left,
.CondensedTribesCarousel .right,
.SuggestedTopicsBundle .CarouselItem,
.SuggestedTribesBundle .CarouselItem,
.buSTMF,
.clXwS,
.duzDkw,
.feed_card_on .AddQuestionPrompt,
.new_user_checklist,
.threaded_comments.full_width_comments,
.u-badge-border--white,
.ui_layout_content_thumbnail .ui_layout_thumbnail,
:focus {
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

.HyperLinkPreview .hyperlink_description {
	display: block!important;
	overflow: hidden;
	text-overflow: inherit!important;
	white-space: pre-line!important;
	word-break: normal!important;
}

/*___ Fonts ________________________________________________________________________*/

.TribeMemberListItem .member_item .user,
code.prettyprint ol.linenums,
pre.prettyprint ol.linenums {
	font-size: 21px!important;
}

.HyperLinkPreviewCompact .hyperlink_description,
.u-serif-font-main--regular {
	font-size: 19px!important;
}

.CredibilityFacts,
.CredibilitySection,
.TribeMemberListItem .member_item .credential,
.comments_preview_toggle .comment_snippet .NameCredential,
.comments_preview_toggle .comment_snippet .comment_preview_text,
.comments_preview_toggle .comment_snippet .user,
.u-sans-font-main--regular {
	font-size: 17px!important;
}

.Notif .timestamp {
	font-size: 15px!important;
}

selector {
	line-height: 19px!important;
}

/*___ Remove Items _________________________________________________________________*/

.Carousel .left .arrow.nav:after,
.Carousel .right .arrow:after,
.CondensedTribesCarousel .left .arrow.nav:after,
.CondensedTribesCarousel .right .arrow:after,
.HyperLinkBundle.Bundle,
.image_ad,
.outer_content_box.feed_expand.home_feed_ad {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

[id*="left_col_inner"] {
	left: 1%!important;
}

.layout_3col_left+.layout_3col_center,
.layout_3col_left {
	margin-left: 0;
}

/*___ Margins ______________________________________________________________________*/

.Notif.unseen,
.jsZUJw,
.profile_wrapper {
	margin: 12px!important;
}

.CarouselBundle .carousel_item,
.comments_preview_toggle,
.ui_story_card {
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

.layout_3col_right,
[id*="left_col_inner"],
.CarouselBundle .carousel_item,
.feed_card_on .layout_3col_center,
.feed_item,
.fixable_fixed,
.jsZUJw,
.layout_3col_center,
.profile_wrapper {
	padding: 12px!important;
}

.layout_3col_left {
	padding: 29px!important;
}

selector {
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

body {
	max-width: none!important;
}

selector {
	width: auto!important;
}

.mweb_switcher {
	width: 127%!important;
}

.ContentWrapper,
.feed_card_on .layout_2col_main,
.kOhBRw,
.main_feed .CondensedTribesCarousel,
.main_feed .SuggestedTopicsBundle,
.main_feed .SuggestedTribesBundle {
	width: 98%!important;
}

.BusinessPage .header,
.UserPage .header,
.eDUfSZ,
.layout_3col_center,
.profile_wrapper {
	width: 80%!important;
}

.feed_card_on .layout_3col_center {
	width: 71%!important;
}

.feed_card_on .layout_3col_right,
.layout_3col_left {
	width: 10%!important;
}
}

