/* ==UserStyle==
@name           Black Wikipedia
@namespace      github.com/martin-juul/userstyles
@version        1.0.0
@description    Black theme for wikipedia
@author         Martin Juul
==/UserStyle== */

@-moz-document domain("wikipedia.org") {
    * {
        /* Set to subpixel-antialiased on crap displays */
        -webkit-font-smoothing: antialiased;
    }

    body.mediawiki, #mw-page-base, #mw-head,
    #mw-head-base, #content, #content .gallerybox div.thumb,
    div.thumbinner {
        background-color: #000;
    }

    li.gallerybox div.thumb, div.thumbinner {
        border-color: #292828;
        border-radius: 6px;
    }

    #siteSub, .hatnote, li, dl, dd, #searchInput {
        color: #ccc;
    }

    /* Search */

    #searchInput {
        background-color: #000;
        border-radius: 12px;
        border-color: #161616;
    }

    .suggestions-result, .suggestions-results, .suggestions-special {
        background-color: #000;
        color: #fff;
        border-radius: 6px;
        border-color: #222;
    }

    .suggestions-special {
        border-radius: unset;
    }

    /* Links */
    a, .vectorTabs li a, #mw-panel .portal .body li a:visited, label.toctogglelabel {
        color: #f59f00;
    }

    a:visited, #mw-panel .portal .body li a:visited {
        color: #ffcd5a;
    }

    /* Top buttons (article, talk, read, edit, view history) */
    .vectorTabs {
        background-image: unset !important;
    }

    .vectorTabs li {
        background-image: unset !important;
        background-color: #0b0b0b !important;
    }

    .vectorTabs li.selected {
        background-color: #222 !important;
    }

    .vectorTabs .selected a, .vectorTabs .selected a:visited, .mw-content-ltr .thumbcaption {
        color: #fff;
    }

    /* Left Nav */
    .mw-interlanguage-selector {
        border-color: #4d4c4c;
        background-color: #000;
        color: #fff;
        border-radius: 15px;
    }

    /* Article */
    h1, h2, h3, h4, h5, h6, p {
        color: #fff;
    }

    table {
        background-color: #000 !important;
        color: #fff !important;
        border-color: #4d4c4c !important;
        border-radius: 6px;
        padding: 12px;
        border-width: 1.33px !important;
    }

    th {
        background-color: #000 !important;
    }

    /* ToC */

    #toc {
        background-color: #000;
        border-color: #4d4c4c !important;
        border-radius: 6px;
        border-width: 1.33px !important;
    }

    .tocnumber {
        color: #ccc;
        font-weight: 600;
    }
}

@-moz-document domain("lmaobox.net") {
h1 {
    font-family: '/*[[h1-font]]*/' !important;
    font-weight: 400;
}

* {
    font-family: '/*[[main-font]]*/' !important;
}

:root {
    --main-color: /*[[main-color]]*/;
    --alt-color: /*[[alt-color]]*/;
    --hover-color: /*[[hover-color]]*/;
    --accent: /*[[accent]]*/;
    --accent-hover: /*[[accent-hover]]*/;
}

body {
    background: var(--main-color);
}

#Body, #Frame {
    background: var(--main-color);
}

.HomepageTitle {
    color: #ccc;
    font-size: 20pt;
    text-align: center;
    background: var(--alt-color);
    border-radius: 5px;
    border: 1px solid var(--hover-color);
}

.Button.Primary {
    background: var(--accent);
    border-radius: 5px;
    text-shadow: 0 0 0 #0000;
    transition: all 0.25s;
    border-color: transparent !important;
    box-shadow: none;
}

.Button.Primary:hover {
    background: var(--accent-hover);
    color: #eee;
    border: 1px solid var(--alt-color);
    border-radius: 5px;
}

.Sprite, .OptionsTitle {
    filter: invert(0.9);
}

span {
    font-family: 'Noto Sans';
}

.Wrap {
    font-family: 'Noto Sans';
}

.container {
    color: #ccc;
}

.navbar-inverse .navbar-inner {
    background-image: none;
    background-color: var(--alt-color);
}

.navbar-inverse .nav .active > a {
    background-image: none;
    background-color: var(--main-color);
}

.navbar-inverse .nav .active > a:hover {
    background: var(--hover-color);
}

a {
    color: var(--accent);
}

a:hover, a:focus {
    color: var(--accent-hover);
}

.navbar {
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
}

input.InputBox, input.InputBox:focus, .TextBox, .TextBox:focus {
    color: #eee;
    background: var(--alt-color);
}



.DataList .ItemDiscussion {
    background: transparent;
    border: 1px solid var(--hover-color);
    border-radius: 5px;
}

.Read {
    background: var(--alt-color) !important;
}

.Read .Options::before {
    content: 'Read';
    color: #888;
}

.New .Options::before {
    content: 'Unread';
    color: #888;
}

.Unread .Options::before {
    content: 'New comment';
    color: #888;
}

li.Announcement {
    background: var(--hover-color) !important;
}

.Bookmark {
    filter: invert(1);
}

.Tag {
    background: var(--hover-color);
    border: 1px solid var(--alt-color);
    border-radius: 5px;
    color: #ccc;
}
.Bookmark:hover {
    filter: invert();
}

.Count {
    background-color: var(--hover-color) !important;
    border-radius: 5px;
    border: 1px solid var(--alt-color);
}

#menu, .container {
    max-width: 100%;
}

#Head .Row {
    text-align: center;
    margin-bottom: 50px;
}

#Head .Row a img {
    margin: 10px;
    border-radius: 5px;
}

#Head .Row a {
    position: relative;
    top: 42px;
    margin-bottom: 20px;
}

.Pager .Highlight {
    color: #ccc;
}

.ReactSprite {
    filter: invert(0.7);
}

.Button {
    border-color: transparent !important;
    box-shadow: none;
}

.Button.DraftButton, .Button.PreviewButton {
    background: var(--alt-color);
    color: #ccc;
    text-shadow: none;
    border: 1px solid var(--main-color) !important;
    outline: none;
    border-radius: 5px;
    transition: all 0.25s;
}

.Button.DraftButton:hover, .Button.PreviewButton:hover {
    background: var(--hover-color);
    transition: all 0.25s;
}

.PageTitle h1 {
    background: var(--alt-color);
}

/* fix activities */

span.ToggleFlyout:nth-child(1) > div:nth-child(3) {
    height: auto;
}

.Activities {
    display: block;
}

/* popout theming */

.FlyoutMenu {
    background: var(--alt-color);
    color: #ccc;
}

div.MenuItems {
    background: var(--alt-color);
    color: #ccc !important;
}

div.MenuItems > ul:nth-child(1) > li:nth-child(1) > a:nth-child(1), .SignInOutWrap > a:nth-child(2) {
    color: #ccc !important;
}

.Flyout::before, .Flyout::after {
    content: "";
    border-right: 7px solid transparent;
    border-left: 7px solid transparent;
    border-bottom: 7px solid #444;
    top: -7px;
    position: absolute;
}

.PopList a {
    color: var(--accent);
}

.Category-russian-, .Category-brazil-discussion, .SpEditProfile {
    display: none;
}

div.Popup .Body {
  background: var(--alt-color);
  color: #ccc !important;
  border-radius: 5px;
}

.Border {
  background: #0000;
}

.AlertMessage {
  border: 2px solid #deddaf;
  border-radius: 5px;
}

.Close > span:nth-child(1) {
  filter: invert(0.7);
}

.Overlay {
  background-color: #0008;
}

.youtubelink img {
    border-radius: 5px;
}
}

