  }

#history
  {
    background-color: #4853329c;
    width: 100%;
    margin-left: 10px;
  }

#parameter /* параметры */
  {
    position: fixed;
    width: 170px;
    top: 20px;
    left: 0px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
  }


a#parameters.toggle  /* "параметры и навыки" */
  {
	 text-align: center;
     font-size: 14px;
  } 

