data.css:
@-moz-document url-prefix("https://gamepress.gg/dragalialost"), url-prefix("https://dragalialost.gamepedia.com/"), url-prefix("https://mushymato.github.io/"), url-prefix("https://wildshinobu.pythonanywhere.com/"), url-prefix("https://b1ueb1ues.github.io/dl-sim-vue/#/") {
html {
    filter: invert(0.95) hue-rotate(180deg);
    -webkit-filter: invert(0.95) hue-rotate(180deg);
    -moz-filter: invert(0.95) hue-rotate(180deg);
    -ms-filter: invert(0.95) hue-rotate(180deg);
    -o-filter: invert(0.95) hue-rotate(180deg);
    background:black;
}
img, embed, video, canvas, iframe, svg, span.Emoticon, span.emoji, i.emoji, div#p-logo {
    filter: invert(1) hue-rotate(180deg) !important;
    -webkit-filter: invert(1) hue-rotate(180deg) !important;
    -moz-filter: invert(1) hue-rotate(180deg) !important;
    -ms-filter: invert(1) hue-rotate(180deg) !important;
    -o-filter: invert(1) hue-rotate(180deg) !important;
}
}

@-moz-document url-prefix("https://www.youtube.com"), url-prefix("http://www.youtube.com") {
/* Shrink top YT search bar and fix margins */
#container.ytd-masthead, #guide-content.ytd-app #header {
    height: 3.8vh;
}
#header.ytd-browse #header, #guide-spacer.ytd-app, ytd-watch-flexy.ytd-page-manager:not([fullscreen]) {
   margin-top: 4.0vh !important;
}
#guide-content.ytd-app #header, #page-manager.ytd-app {
    margin-top: 0px;
}


/* Resize the player in theater mode only */
ytd-watch-flexy[theater]:not([fullscreen]) #player-theater-container.ytd-watch-flexy {
    max-height: 96vh;
    height: 96vh;
}
}

@-moz-document domain("www.zive.cz"), domain("www.mobilmania.cz"), domain("connect.zive.cz"), domain("jnp.zive.cz"), domain("vtm.zive.cz"), domain("doupe.zive.cz") {
body {
    color: #fff;
}
html, body, .common-menu, .tm-r1, .cs-cnt-wrapper {
    background: none;
    background-color: #151515 !important
}
.tm-r1 .cs-cnt, .cs-cnt-cl, .cs-cnt-main, .box-cex, .ar-detail .ar-society-networks .ar-society-networks-wrapper, .box, .fs-os {
    background: none;
    background-color: #333 !important;
}
.cs-cnt {
    background: #424242;
}
.forum-row-wrapper .forum-head, .forum-text {
    border-top: 4px solid rgba(255, 255, 255, 0.3);
    background-color: #454545 !important;
}
.forum-row-wrapper .forum-head, .forum-row-wrapper .forum-text {
    border-left: 1px solid rgba(255, 255, 255, 0.3);
    border-right: 1px solid rgba(255, 255, 255, 0.3);
}
.ar-detail .ar-annotation, .ar-detail .ar-content, .ar-detail .ar-society-networks .ar-info .ar-date, a, .ar-chapters a.ar-btn, .ar-chapters .ar-btn-comments, .ar-chapters .ar-btn-comments a, .ar-content .ar-tags, .ar-content .ar-tags a, .ar-detail .ar-content .ar-tags a {
    color: #999 !important;
}
#Billboard-bottom-1 {
    background: none !important;
}
a {
    color: #ccc;
}
.bx-head a, .bx-cnt {
    color: #fff !important;
}
.forum-main div[id^='collapseChilds'] {
    display: inline !important;
}

div[id^='bodyPart'] {
    display: inline !important;
}

.ar-detail .ar-content .article-template-spliter {
    display: none;
}
.consult-results tr.odd td {
    background: #2d2d2d;
}
.ar-author > a > img { 
    filter: invert(0.8) hue-rotate(170deg);
}
.cs-cnt-cl-r {
    display: none;
}
.branding-wrapper {
    background: #151515 !important
}
.cs-cnt-wrapper, .christbar ul {
    background: inherit !important 
} 
#footer-global {
	display: none
}
.cs-cnt.fs-os {
    display: none;
}
}

@-moz-document domain("www.mobilmania.cz") {
#search-text::placeholder {
    color: #1781f2;
}
.ar-detail h1 {
    color: #69aef8;
}
.ar-detail .ar-content h2, .ar-detail .ar-content h3, .ar-detail .ar-content h4 {
    color: #4186cf;
}

.ar-list .ar-row .ar-txt span.ar-comments a::before {
    filter: brightness(1.7);
}
}

@-moz-document domain("connect.zive.cz") {
.ar-detail .ar-content a, .forum-social-agree {
    color: #23a3bf !important;
}
.tm-r1 .logo {
    filter: invert(0.8) hue-rotate(170deg);
}
}

@-moz-document domain("stereo.ru") {
/*-- ИНФОРМАЦИЯ И ПОЯСНЕНИЯ -*/

/*-- Версия стиля 0.2.0 -*/

/*-- ИНФОРМАЦИЯ И ПОЯСНЕНИЯ -*/

/*
1. Что бы обновить стиль, в расширении stylus нажмите кнопку "менеджер" и рядом с названием сайта нажмите кнопку проверить обновления. 
Если вы вносили какие то изменения в основной код, то предварительно скопируйте свои правки и после обновления вставьте их обратно.
*/

/* ----------------------------------------------------------- */

html {
    background-color: #272a2d;
}
.article-preview__title {
    color: #adadad;
}

.article-preview__title:hover {
    color: #ccc;
}

.article-preview__text, .article-preview__text p {
    color: #ccc;
}

.news-card__title {
    color: #ccc;
}

.news-card:hover .news-card__title span {
    color: #999;
}

.subscription-block {
    background: #33363a;
}

.sidebar__block {
    float: left;
    clear: both;
    background-color: #33363a;
}

.subscription-block__header, .block .block_description a, .theme a, .m-news-link {
    color: #f2720b;
    font-size: 16px;
    text-decoration: none;
}

.subscription-block__header:hover, .block:hover .block_description a:hover, .theme a:hover, .m-news-link:hover {
    color: #fda055;
    font-size: 16px;
    text-decoration: none;
}

.subscription-block .subscription-block__footnote {
    color: #ccc;
}

.block ul li a.c-link {
    color: #ccc;
    text-decoration: none;
}

.user {
    color: #b0b0b0;
}

.user:hover {
    color: #ccc;
}

a.counter {
    color: #b0b0b0;
}

time {
    color: #b0b0b0;
}

.volume {
    color: #b7422d;
}

nav.pagination .pages a {
    color: #999;
}

nav.pagination .pages .current {
    color: #eee;
}

.header__search-form, .header__search-form input, .header__user-block {
    background-color: #272a2d;
    color: #ccc;
}

.header__block  {
    background-color: #272a2d;
}

.header__logo svg {
    filter: invert(1);
    filter: brightness(4.5);
}

.subscription-block input[type=text] {
    border: 1px solid #33363a;
    background-color: #272a2d;
}

.subscription-block p {
    color: #ccc;
}

.footer_nav a {
    color: #999;
}

.article .content {
    color: #ccc;
}

.ca_text p {
    color: #b9b9b9;
}

.ca_text blockquote {
    color: #b9b9b9;
    border-left: 5px solid #757a80;
}

.ca__link-to-parent {
    color: #aaa;
}

.comment {
    border-top: 1px solid #545454;
}

.article__rating {
    background-color: #33363a;
}

.article__rating .vote {
    border: 1px solid #545454;
    background-color: #272a2d;
}

.vote {
    color: #558aab;
}

.content-shares {
    filter: brightness(0.8);
}

.registration_description {
    color: #999;
    border-right: 1px solid #5a5a5a;
}

.form label {
    color: #999;
}

.form input, .form textarea {
    border: 1px solid #33363a;
}

.button {
    background-color: #909090;
}

a {
    color: #48a9ca;
}

.subscription-block__submit {
    border: 1px solid #33363a;
    background-color: #909090;
}

.header__nav a {
    color: #ccc;
}

.page-description__title, .page-type__description {
    color: #ccc;
    background-color: #33363a;
}

#scroll-driver a:hover {
    color: #ccc;
}

.timeline .cell a:hover {
    color: #ccc;
}

.question-preview__title {
    color: #b9b9b9;
}

.qa-prev-text-link, .qa-prev-text-link:hover {
    text-decoration: none;
    color: #ccc;
}

.question-preview__title:hover {
    color: #ccc;
}

.profile_preview a.user_link {
    color: #d9d9d9;
}

.user_by a {
    color: #f2720b;
}

article h1.article-news__title {
    color: #ccc;
}

.profile_preview {
    border: 1px solid #676767;
}

.theme_block .theme_title a {
    color: #ccc;
    text-decoration: none;
}

.block ul li a.c-link:hover {
    color: #eee;
}

.theme_block .theme_title a:hover {
    color: #eee;
}

.warning__auth-text {
    color: #ccc;
}

.block ul li .b-rating {
    color: #b7422d;
}

.theme_block .theme_description {
    color: #d0d0d0;
}

.misspelled__notice span {
    color: #ccc;
}

.question__title {
    color: #cacaca;
}

.question__body p {
    color: #ccc;
}
}

