@-moz-document url(https://rooster.hetmml.nl/) {
form#search, body {
    background-color: #303030 !important;
}
html {
    color: white !important;
}
body.no-input #search input[type="search"] {
    background-color: #515151;
    caret-color: white;
}
body.no-input #search input::placeholder {
    color: #959595;
}
body.no-input .tooltip, body.no-input .tooltip::before {
    background-color: #515151;
}
#search input[type="search"] {
    background-color: #515151;
    transition: .125s;
}
#search input[type="search"]:focus {
    background-color: #5e5e5e;
    caret-color: white;
}
input::placeholder, input:focus::placeholder {
    color: white;
}
#search #overflow-button {
    color: #757575;
}
.mdl-menu__outline {
    background: #515151;
}
.mdl-menu__item {
    color: white;
}
#search input[type="search"]:focus {
    color: #FFF;
}
.autocomplete-wrapper {
    background-color: #515151;
}
.autocomplete li:hover {
    background-color: #5E5E5E;
}
#week-selector {
    background-color: #474747;
}
#week-selector button:focus, #search #overflow-button:focus, #search .fav:focus {
    background-color: #4d4d4d;
}
}

@-moz-document domain("vk.com") {

/* ФИД ЛЕНТА, ПРАВЫЙ БЛОК*/
#friends_possible_block,
#ui_rmenu_news,
#ui_rmenu_news_list,
#ui_rmenu_recommended,
#ui_rmenu_articles,
#ui_rmenu_updates,
#feed_filters
{
	display: none !important;
}

/* ФИД ЛЕНТА ВООБЩЕ */
#main_feedsd 
{
	display: none !important;
}

/* НОТИФИКАЦИИ  */

.im-page--top-notice
{
	display: none !important;
}

/* НАХУЙ ТЕБЯ И ТВОЁ МЕНЮ СЛЕВА*/
#l_fav,
#l_mk,
#l_ap,
#l_vid,
#l_ph,
#l_gr,
#l_fr,
#l_nwsf
{
	display: none !important;
}

/* НАВОДЯШКИ НА ТЕКСТ, ХОВЕР*/
.left_label 
{
    transition: all 0.5s linear;
    opacity: 0.0;
}

.left_label:hover
{
    opacity: 1.0;
    transition: all 0.5s linear;
}

/* ФУТЕР СЛЕВА */
.left_menu_nav_wrap
{
	display: none !important;
}

/*  СКРЫТО, НО ПРИ НАВЕДЕНИИ ПОКАЗЫВАЕТСЯ */

/* ПАБЛИКИ НА ГЛАВНОЙ СТРАНИЦЕ ПОЛЬЗОВАТЕЛЯ И ВИДЕО */
.page_block > #profile_idols,
#profile_idols,
#profile_videos,
#profile_friends
{
    transition: all 0.5s linear;
    opacity: 0.0;
}

#profile_idols:hover,
#profile_videos:hover,
#profile_friends:hover
{
    opacity: 1.0;
    transition: all 0.5s linear;
}

/* МУСОР */
.profile_rate_warning,
.tt_w,
.lastfm_status
{
	display: none !important;
}

.placeholder { 
    opacity:0 !important;
}

.no_current_info {
    visibility: hidden;
}


/* СПИСОК УЧАСТНИКОВ И  В ПАБЛИКАХ*/
#public_followers,
#public_albums,
#public_videos,
#public_audios,
#public_events,
#public_contacts,
#public_links,
#group_topics,
#group_followers,
#group_albums,
#group_videos,
#group_audios,
#group_events,
#group_contacts,
#group_links,
#group_topics,
#group_apps,
#profile_common_friends,
#profile_friends_online
{
    transition: all 0.5s linear;
    opacity: 0.0 !important;
}

#public_followers:hover,
#public_albums:hover,
#public_videos:hover,
#public_audios:hover,
#public_events:hover,
#public_contacts:hover,
#public_links:hover,
#public_topics:hover,
#group_followers:hover,
#group_albums:hover,
#group_videos:hover,
#group_audios:hover,
#group_events:hover,
#group_contacts:hover,
#group_links:hover,
#group_topics:hover,
#group_apps:hover,
#profile_common_friends:hover,
#profile_friends_online:hover
{
    opacity: 1.0 !important;
    transition: all 0.5s linear;
}

/* IM */
/* LEFT MENU */
#ui_rmenu_unread,
._im_last_act
{
	display: none !important;
}

/* UNREAD */
._im_mess_unread,
._im_mess_unread:before,
.nim-dialog--inner-text
{
    background: transparent !important; 
}

#im_dialogs
{
    padding-bottom: 0px;
}

/* НА СТРАНИЦЕ ПОСЛЕДНИЙ ЗАХОД */
#profile_time_lv {
	display: none !important;
}

.post_like,
.post_share_icon,
.reply_link_wrap,
.page_cover,
.post_share_count,
.post_like_count,
#public_market,
.feed_friends_recomm
{
	display: none !important;
}
}

@-moz-document domain("finobe.com"), domain("fi.nobelium.xyz"), domain("www.finobe.com") {
body {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif!important;
    background: white;
}
.navbar-light.bg-faded {
    background: url(http://www.roblox.com/images/RevisedHeader/bg-rbx_header.png) repeat-x;
    height: 36px;
}
.navbar-light .navbar-nav .nav-link {
    color: white;
    font-weight: bold;
    font-family: "Arial";
    font-size:10pt
}
.navbar-light .navbar-nav .nav-link strong {
    font-weight: unset;
    font-family: Arial;
    font-size:10pt
}
.navbar-light .navbar-nav .nav-link:hover {
    background: url(http://www.roblox.com/images/RevisedHeader/bg-main_menu_hover.png) repeat-x;
    color: white;
}
.navbar-brand {
    background: url(http://www.roblox.com/images/RevisedHeader/btn-rbx_logo.png);
    width: 116px;
    height: 35px;
    margin-right: 4px;
    color: transparent!important;
}
.navbar-brand:hover {
    background-position: bottom;
}
.navbar-brandimg {
    display: none!important;
}
.nav-scroller {
    padding-left: 110px;
}
.nav-scroller {
    background: #191919!important;
    border: none;
    height: 25px!important;
    text-align: center!important;
}
.nav-scroller-inner>a.nav-link {
    font-size: 14px;
    font-weight: 400;
    margin-top: -9px;
    color: white;
}
.navbar-expand-lg>.container {
    width: 1000px;
}
.navbar-light .navbar-nav .show>.nav-link {
    background-color: #efefef!important;
    border-top: 1px solid #777;
    border-left: 1px solid #777;
    border-right: 1px solid #777;
    color: black!important;
}
.navbar-nav .dropdown-menu {
    border: 1px solid #777;
    border-top: none;
    background-color: #efefef!important;
    border-radius: 0px;
    top: 92%;
    padding: 0px;
}
.dropdown-item:hover {
    border-radius: 0px;
    background-color: #d8d8d8;
}
.dropdown-item {
    margin-bottom: 0px!important;
}
.game-card {
    border-radius: 0px;
    transition: none;
    box-shadow: none;
    width: 200px;
    height: 205px!important;
    margin-left: 13px;
    background: transparent;
    overflow: hidden;
    border: 1px solid transparent;
}
.game-card img {
    width: 200px!important;
    height: 90px!important;
    border-radius: 0px;
}
.game-card:hover {
    border: 1px solid #777;
    box-shadow: none;
    background: white;
}
.btn, .page-link {
    transition: none;
    border: 1px solid #777;
    color: #666666;
    font-weight: bolder;
    border-radius: 0px!important;
    background-image: linear-gradient(#FEFEFE, #DEDEDE);
    text-transform: none;
}
.btn:hover, .page-link:hover {
    background-image: linear-gradient(#FEFEFE, #C7D5FF);
    border: 1px solid #777;
    box-shadow: none;
    color: #777777;
}
h3 {
    color: #343434;
    letter-spacing: -1px;
    font-size: 32px;
    font-weight: bold!important;
}
.btn-success {
    border-color: #007001;
    background-color: #007001;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    color: white!important;
}
.btn-success:hover {
    border-color: #007001;
    background-color: #007001;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    color: white!important;
    background-position: left -128px;
}
.card {
    border: none;
}
.catalog-card>.card {
    border-radius: 0px;
    transition: none;
    border: 1px solid transparent;
}
.catalog-card>.card:hover {
    border: 1px solid #ccc;
    box-shadow: none!important;
}
.footer>.container {
    background: #123f83;
    font-size: 13px;
    color: White;
    margin: 0;
    width: 100%;
    padding: 2em;
    border: none;
}
.footer .container {
    max-width: 100000px;
}
.nav-link .badge-danger {
    color: Black;
    border: 1px solid Black;
    background-color: White;
}
.catalog-no-overflow-plz a {
    color: #0055B3;
}
.page-item.active .page-link, .page-item.disabled .page-link, .btn-primary.disabled {
    border: 1px solid #777;
    color: #777;
    background: #efefef;
}
a {
    color: #0055B3;
}
.btn-danger {
    border-color: #565656;
    background-color: #565656;
    background-image: url(https://static.rbxcdn.com/images/Buttons/StyleGuide/bg-btn-gray.png);
    color: white;
}
.btn-danger:hover {
    border-color: #565656;
    background-color: #565656;
    background-image: url(https://static.rbxcdn.com/images/Buttons/StyleGuide/bg-btn-gray.png);
    color: white!important;
    background-position: left -128px;
}
.btn-primary {
    border-color: #0852b7!important;
    color: white;
    background-color: #0852b7;
    border: 1px solid;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-blue.png);
}
.btn-primary:hover {
    border-color: #0852b7!important;
    color: white;
    background-color: #0852b7;
    border: 1px solid;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-blue.png);
    background-position: left -128px;
}
.home-main-content {
    background: url(https://imagizer.imageshack.com/img923/9208/qyFcnu.png) no-repeat 50%;
    background-size: cover;
    margin: 0;
    padding: 0;
    height: 300px;
    color: hsla(0,0%,100%,.92);
    display: flex;
    margin-bottom: .5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.2;
    color: black;

}
.h1, h1 {
    font-size: 2.1875rem;
    font-weight: bold;
}
.alert-info {
    background: #FDA702;
    color: #fff;
    border: 1px solid gray;
    font-size: 16px;
    font-weight: bold;
}
.alert-skinny {
