    background-size: cover !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
}

::-webkit-scrollbar {
	width: 10px !important;
}

::-webkit-scrollbar-track {
	background: #262626 !important;
}

::-webkit-scrollbar-thumb {
	background: #CB7119 !important;
}

::-webkit-scrollbar-thumb:hover {
	background: #B25D0E !important;
}

@keyframes gradient {
    to {
      background-position: 200% center;
    }
  }

/*header*/
.header {
	background: rgba(0,0,0,0.8) !important;
}

header .logo > img {
display: none;
}

header .logo {
	content: url(https://i.imgur.com/g1djIPI.png);
}

.social a:nth-child(odd) {
	color: #B25D0E !important;
}

.social a:nth-child(even) {
	color: #CB7119 !important;
}

.social a:hover {
	color: #874D24 !important;
}

.statusinfo {
	color: #CB7119 !important;
}

.friend-access-dropdown {
	background: rgba(0,0,0,0.8) !important;
    border: none !important;
    border: 1px solid #CB7119 !important;
}

.friend-access-switch {
	border: none !important;
}

.friend-access-switch-ip {
	color: #CB7119 !important;
}

.user .username {
	color: #CB7119 !important;
}

.usericon>a>i {
	color: #CB7119 !important;
}

.usericon>a>i:hover {
	color: #B25D0E !important;
}

/*Sidebar*/
.sidebar {
	background: rgba(0,0,0,0.5) !important;
}

/*menu*/
.navigation {
	background: rgba(0,0,0,0.8) !important;
}

.navigation .item {
	border: none !important;
}

.navigation .item:nth-child(odd) {
	color: #CB7119 !important;
}

.navigation .item:nth-child(even) {
	color: #B25D0E !important;
}

.navigation .item:hover {
    color: #874D24 !important;
    background: rgba(203,113,25,0.1) !important;
}

.navigation-toggle {
	color: #B25D0E !important;
}

.navigation-toggle:hover {
	color: #874D24 !important;
}

.navigation .item .fas {
	font-weight: 10 !important;
}

/*
orange:
rgba(203,113,25,0.2)

Darker orange:
rgba(178,93,14,0.2)

*/

.navigation .item.active {
	background: linear-gradient(to right, rgba(203,113,25,0.3) 20%, rgba(178,93,14,0.2) 40%, rgba(178,93,14,0.2) 60%, rgba(203,113,25,0.3) 80%);
  background-size: 200% auto;
  color: #33a9ef;
  animation: gradient 5s linear infinite;
}

/*content*/

.content {
	background: rgba(0,0,0,0.5) !important;
}

.content h1 {
	color: #CB7119 !important;
    text-shadow: 0 0 2.5px black !important;
}

.content h2 {
	color: #B25D0E !important;
    text-shadow: 0 0 2.5px black !important;
}

.list-item {
	background: rgba(0,0,0,0.5) !important;
}

.list-name {
	color: #CB7119 !important;
}

.list-icon .fas {
	color: #CB7119 !important;
    font-weight: 10 !important;
}

.permissions-title h3 {
	color: #CB7119 !important;
    text-shadow: 0 0 2.5px black !important;
}

.toggle-label {
	color: #B25D0E !important;
    text-shadow: 0 0 2.5px black !important;
}

.toggle label {
	background: rgba(0,0,0,0.5) !important;
    border: 2px solid rgba(0,0,0,0.5) !important;
}

.list-input {
	background: rgba(0,0,0,0.5) !important;
}

.list-input input {
	color: #CB7119 !important;
}

