@-moz-document domain("seznamzpravy.cz") {
/* 
---------------INFO----------------------------------- 
autor: Vilém Danzig
verze 0.2 ; 2020

pouzivane barvy:

seda: #d5d5d5
svetla seda jako nahrada textu: #d0d0d0
zakladni modra: #5d79a0
vyrazna modra: #3e8aa4
tmava modra: #446271



-------------------------------------------------------
*/


body {
    background-color: #ffffff;
    color: #111;
}



/* cary */
.d_ha:after {
    display: block;
    position: absolute;
    left: 0;
    bottom: 1px;
    width: 100%;
    height: 1px;
    content: "";
    background: #d5d5d5;
}

.e_B {
    color: #5d79a0;
    text-decoration: none;
    cursor: pointer;
}

.f_dY {
    padding: 1rem;
    background-color: #eee;
}
.e_ah {
    margin-bottom: 1rem;
    border-top: 4px solid #5d79a0;
    padding-top: .5rem;
}
.e_af {
    font-size: 1rem;
    font-weight: 800;
    line-height: 1.5rem;
    letter-spacing: 2px;
    color: #446271;
    text-transform: uppercase;
}
.d_g6 .e_B:focus .d_g7:after, .d_g6 .e_B:hover .d_g7:after, .d_g6 .d_g3 .d_g7:after {
    display: block;
    position: absolute;
    bottom: 0;
    z-index: 4;
    width: 100%;
    height: .25rem;
    content: "";
    background: #5d79a0;
}
.d_hP:not(.h_bQ) {
    font-size: .875rem;
    line-height: 1.25rem;
    color: #000;
}
.d_J {
    margin-bottom: 1rem;
    color: #5d79a0;
    text-transform: uppercase;
}
.f_eM {
    border-top: 1px solid #d5d5d5;
}
.d_f6 {
    margin-top: .75rem;
    border-top: 1px solid #d5d5d5;
    padding-top: .75rem;
}

/* tlacitko */
.e_y .e_B {
    display: inline-block;
    padding: 1rem 2.5rem;
    font-size: .75rem;
    font-weight: 800;
    line-height: 1rem;
    letter-spacing: 2px;
    color: #fff;
    text-transform: uppercase;
    background: #5d79a0;
}

/* focus tlacitka */

.e_y:hover .e_B:hover {
    display: inline-block;
    padding: 1rem 2.5rem;
    font-size: .75rem;
    font-weight: 800;
    line-height: 1rem;
    letter-spacing: 2px;
    color: #fff;
    text-transform: uppercase;
    background: #4c7dc1;
}


.d_gW {
    border-top: 1px solid #d5d5d5;
    padding-top: 1.5rem;
}
.e_B:focus, .e_B:hover {
    color: #5d79a0;
    text-decoration: underline;
}
/* misto-obrazky */
.e_aD {
    background-color: #d0d0d0;
}
.e_aH {
    background-color: #d0d0d0;
}
.d_it {
    margin-bottom: 1rem;
    border-top: 1px solid #d0d0d0;
    border-bottom: 1px solid #d0d0d0;
    padding: 1rem 0;
}
/* tag aktualizovano */

.e_ay {
    background-color: #3e8aa4;
}

/* focus titulku */

.e_B:focus .e_J, .e_B:hover .e_J {
    color: #5d79a0;
}

/* focus v hlavnim menu */

.d_g2 .e_B:focus, .d_g2 .e_B:hover {
    color: #414670;
}

/* focus autor v clanku */
.f_b3 .e_B:focus, .f_b3 .e_B:hover {
    color: #5d79a0;
}

/* externi odkaz */
.d_aV .f_do a {
    display: inline-block;
    border-bottom: 1px solid #446271;
    line-height: 1;
    color: #446271;
}

/* externi odkaz hover */

.d_aV .f_do a:hover {
    display: inline-block;
    border-bottom: 1px solid #5d79a0;
    line-height: 1;
    color: #5d79a0;
}

/* datum a cas */
.d_gX {
    display: flex;
    margin-bottom: .5rem;
    padding: 0 1rem;
    flex-wrap: wrap;
    font-size: .75rem;
    letter-spacing: 1px;
    color: #5d79a0;
}


/* cara pod souvis clanky */
.e_ag {
    border-bottom: 1px solid #5d79a0;
    padding-bottom: 0.5rem;
}

/* pozadi top banneru */
.d_fl .f_aT {
    background-color: #fff;
}

/* paticka */
.d_hy {
    margin: 0 auto 1.5rem;
    color: #686868;
}

/* odkazy na jine weby seznamu dole */

.d_iz {
    color: #5d79a0;
}

/* boxy jako nahrada textu a obrazku pri loadingu */
.e_i {
    background-color: #d0d0d0;
}

/* barva hlavniho loga */

.d_hb path {
    fill: #262626;
}

/* tri male vodorovne cary pod sebou vedle odkazu na souvisejici clanky */
.f_cw path {
    fill: #5d79a0;
}

/* jmeno autora v jeho profilu */

.d_gJ {
    color: #4d4d4d;
}


/* cara v paticce */
.d_a1 {
    margin: 0 1rem 1rem;
    border-bottom: 1px solid 
    #d0d0d0;
    padding-bottom: 1rem;
}

/* cara mezi clanky v tablet modu */
.d_hm {
    margin-top: 1rem;
    border-top: 1px solid #d0d0d0;
    padding-top: 1rem;
}

/* cas u bezicich udalosti */
.f_bV {
    color: #446271;
}

/* cas u udalosti na titulce */
.f_b7 {
    color: #446271;
}

/* oznaceni dnu u starsich zprav */
.f_eP {
    color: #446271;
}

/* pozadi anket */
.j_jc {
    padding: 1rem;
    background-color: #f1f1f1;
}

/* popis voleb anket */
.f_dJ {
    margin-bottom: .75rem;
    font-size: .875rem;
    font-weight: 600;
    line-height: 1.25rem;
    color: #446271;
}


/* ankety - vyplnena cast progressbaru */
.f_dP {
    height: 100%;
    background-color: #446271;
}

/* ankety - nevyplnena cast progressbaru */
.f_dN {
    height: .625rem;
    flex: 1 1 auto;
    background-color: #b1c0c8;
}

/* ankety - pocet hlasujicich */
.f_dI {
    font-size: .8125rem;
    font-weight: 500;
    line-height: 1.5rem;
    color: #222;
}
}

