@-moz-document domain("tutorialspoint.com") {
/*__________________________________________________________________________________*/

section.toggle,
.col-md-3,
.compete-exams,
.card,
.chat-msg a.sendmsg,
.dev-box,
.menu-active,
.menu-item,
.mui-container .mui-col-md-3,
.question_section,
.selected-reading-boxes .col-md-3 .course-box,
.slick-initialized .slick-slide,
.tp-content-widget .tp-content-icons,
.tut_profile,
ul.menu li a,
ul.sprite li
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

section.toggle:hover,
.col-md-3:hover,
.compete-exams:hover,
.card:hover,
.chat-msg a.sendmsg:hover,
.dev-box:hover,
.menu-active:hover,
.menu-item:hover,
.mui-container .mui-col-md-3:hover,
.question_section:hover,
.selected-reading-boxes .col-md-3 .course-box:hover,
.slick-initialized .slick-slide:hover,
.tp-content-widget .tp-content-icons:hover,
.tut_profile:hover,
ul.menu li a:hover,
ul.sprite li:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img ppp {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

.mui-col-md-6 li,
.mui-col-md-6 p,
.nav-pills>li,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.mui-col-md-6 li:hover,
.mui-col-md-6 p:hover,
.nav-pills>li:hover,
tr:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

.dev-box:hover,
.tp-content-widget .tp-content-icons a img:hover {
	opacity: 1!important;
}

}

