@-moz-document url-prefix("https://music.yandex.ru") {
/*Попытки скрыть рекламные блоки. Вроде бы неудачные*/
iframe,
div[data-b="2"],
div[data-b="18"],
div[data-b="2129"],
div[data-b="20"],
div.d-payment-show {
    display:none
}

