@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spiegel.de") {
/*Lesbare Schrift (OpenSans) */
@font-face {
    font-family: 'Custom';
    src: url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2)
}
:not(code):not(pre):not(#⁠):not(#⁠):not(#⁠) {
    font-family: 'Custom', 'default-icons-desktop', 'FontAwesome', 'ac_iconset', 'fontello', 'pictonic', 'Noticons', 'Dashicons', 'consumer-icons', 'Elusive-Icons', 'Material Icons Extended';
    font-size: 100% !important
}

/* Hintergrund blau */
body {
    background: #253960 url("data:image/png;base64,") top left repeat-x !important;
    color: #fff !important;
}

/* new defaults for all links */
a:link,
.link,
A:link > .headline,
.headline-intro {
    color: #738ab9 !important;
}
a:visited,
.link:visited,
A:visited > .headline {
    color: #354259 !important;
}



/* - Menu-Drawer customizing ------------------------------------------------------------------------------------------------- */
nav[class*="bg-white"]{
    background: #1e2e4d;
    outline: 1.75px solid #d7d7d7;
}


/* - Mainpage customizing ------------------------------------------------------------------------------------------------- */
div[data-area="news-section"], ul[class*="z-10"] {
    background: #253960;
}

/* Headlines über den Titeln > "Mehr zum Thema" */
div[data-area="title-cluster"]  {
    outline: 1.75px solid #d7d7d7; 
    background: #1e2e4d !important;
}

.z-40.bg-white {
    background: #253960d6;
}


/* Kacheln auf der Startseite */
article[class*="bg-white"], div[class*="bg-white"]  {
    background: #253960 !important;
}

/*
div[class*="flex"]  {
    outline: 4px dotted #f00 !important;
    background: #1e2e4d !important;
} */

div[data-size="8-12"] {
    width: auto !important;
    padding: 2px;
    box-shadow: 5px 5px 4px #7284a6;
}

span[class*="text-primary-base"] {
    color: #d7d7d7
}
/* im original orangene Überschriften */
div[data-area="title-cluster"] {
    background: #253960;
}

/*oberster Artikel */
div[data-pos="1:1"] {
    background: #253960;
    font-family: Arial !important;
}
div[data-pos="1:1"] > article > header > h2 > a > span {
    color: #d7d7d7;
}

/* Ausgeblendete Elemente:  headerzeile + Lobo Podcastbox */
div[data-area="header-bar"] {
    display: none !important;
}
section[class*="lg:z-0"] {
    display: none !important;
}


/* - Topic Pages ------------------------------------------------------------------------------------------------- */
.relative { background: #253960; }


article > header * {
    background: #253960;
    margin: 0px !important;
}

div[data-component="MultiboxAccordionSection"] > * {
    background: #7284a6;
}

/* Boxen mit weiterführenden Artikeln */
section[data-area="contentbox"] > div {
        background: #253960;
        outline: 2px solid #d7d7d7;
}

/* Videoboxen im Text etc. */

/* Ausgeblendete Elemente:  Social Media + Footer mit Spielen und Co */
section[data-app-hidden="true"], section[data-component="FurtherReads"] {
    display: none !important;
}
footer{ display: none !important;}

/* 
Logik und Links:

#Element-ID
.class
HTML-Element

|->
    Selectors: https://www.w3.org/TR/selectors/#attribute-selectors



*/
}

@-moz-document url-prefix("http://rce.veeseo.com/iframe/spiegel.php"), url-prefix("http://spiegel.rce.veeseo.com/") {
/* Formatierung von Artikeln mit Bildern auf einzelnen Seiten <= Kommen von RCE.VEESO.COM */
H5 {
    color: #e4e4e4 !important;
}
A:visited > H5 {
    color: #364A7A !important;
}
H4 {
    color: #e4e4e4 !important;
}
SPAN[class="truncate"] {
    color: #e4e4e4 !important;
}
.more {
    color: #e4e4e4 !important;
}
}

@-moz-document url-prefix("http://farm.plista.com/iframeWidget.php?publickey=spiegel") {
/* Formatierung von Artikeln mit Bildern auf einzelnen Seiten <= Kommen von RCE.VEESO.COM */
.itemTitle {
    color: #e4e4e4 !important;
}
A:visited > .itemTitle {
    color: #364A7A !important;
}
H4 {
    color: #e4e4e4 !important;
}
.itemText,
.itemKicker {
    color: #e4e4e4 !important;
}
.itemMore {
    color: #e4e4e4 !important;
}
}

@-moz-document url-prefix("https://re-volt.io")
{

.bg-bright {
  background-color: var(--dark-bg-color);
}

.bg-pattern-bright {
  background-color: var(--dark-bg-color);
  background-image: url("https://re-volt.io/user/themes/bolt/images/pattern-dark.png");
  border: 1px solid #242424;
}

body {
  background: url("https://re-volt.io/user/themes/bolt/images/background/frontend.png") fixed center no-repeat !important;
  background-color: #000000 !important;
  background-image: url("https://re-volt.io/user/themes/bolt/images/pattern-dark.png");
}

div.showcase {
  background-color: var(--dark-bg-color);
}

code {
  background: url("https://re-volt.io/user/themes/bolt/images/pattern-dark.png");
  border: 1px solid #181818;
}

table {
  border: 1px solid #202020;
}

table, td {
  border: 1px solid #202020;
  color: var(--font-color-bright);
}

tr:nth-child(odd) {
  background-image: url("https://re-volt.io/user/themes/bolt/images/pattern-grey-dark.png");
  background-color: #292929;
}

tr:nth-child(even) {
  background-image: url("https://re-volt.io/user/themes/bolt/images/pattern-dark.png");
  background-color: #292929;
}

blockquote {
  background-color: var(--dark-bg-color);
  background-image: url("https://re-volt.io/user/themes/bolt/images/pattern-dark.png");
}

.side {
  background-color: var(--dark-bg-color);
  border: 1px solid #242424;
}

.card {
  color: var(--font-color-bright);
  background-color: #343434;
  box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.1), inset 0 0 30px #363636;
}

.card-style {
  color: var(--font-color-bright);
  background-color: #323232;
}

.tag {
  background-color: rgba(32, 32, 32, 0.3);
}

.admin-link {
  background-color: var(--dark-bg-color);
  background-image: url("https://re-volt.io/user/themes/bolt/images/pattern-dark.png");
  box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.06), inset 0 0 30px rgb(24, 24, 24);
}

@media (min-width: 800px) {
  body {
  background-color: var(--dark-bg-color);
  }
}

h1, h2, h3, h4, h5, h6 {
  color: var(--font-color-bright);
}

.content p {
  color: var(--font-color-bright);
}

/* TOC plugin */
h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
  color: var(--font-color-bright) !important;
}

.admin-link {
  color: var(--font-color-bright);
}

li {
  color: var(--font-color-bright);
}

span.toctitle {
  color: var(--font-color-bright);
}

hr {
  margin: 1.5em;
  border: 2px solid #242424;
}

.events {
  border: 1px solid #242424;
  scrollbar-color: #363939 #282828;
}

.event-card {
  background-color: var(--dark-bg-color);
  color: var(--font-color-bright);
}

.event-today {
  border: solid 2px #3e2440;
  background-color:#45354a;
  color: var(--font-color-bright);
}

.table-row-highlight {
  border: solid 2px #976c1f;
  background-color:#45354a !important;
  color: var(--font-color-bright);
}

.card h3 a {
  color: #f2992e !important;
}

/*Links*/
a {
	color: #f2992e;
}
a:hover {
  text-decoration: underline;
  color: var(--link-darkbg-h);
}

.pagination li {
  border: 2px outset #282828 !important;
  background-color: var(--dark-bg-color) !important;
}

}

