html {
    --scrollbarBG: #202225;
    --thumbBG: #66686D;
}

html::-webkit-scrollbar {
    width: 11px;
}

html {
    scrollbar-width: thin;
    scrollbar-color: var(--thumbBG) var(--scrollbarBG);
}

html::-webkit-scrollbar-track {
    background: var(--scrollbarBG);
}

html::-webkit-scrollbar-thumb {
    background-color: var(--thumbBG);
    border-radius: 6px;
    border: 3px solid var(--scrollbarBG);
}

textarea {
    width: 98.6%!important;
    height: auto;
}

/* Poll */
.pun .poll {
    margin-top: 2em;
    padding-bottom: 1em;
    background-color: #2A2A2A;
    border: 1px solid #857B63;
}

.poll legend {
    font-weight: normal;
}

.poll p {
    font-weight: bold;
}

.pun .poll ol {
    list-style: none outside none;
    padding: 0px;
}

.pun .poll ol label {
    padding: 0px;
    background-color: #F9F5D1;
    border: 0 none;
}

.pun .poll li {
    padding: 0;
    margin-bottom: 0.5em;
}

.pun .poll label {
    padding: 0px;
}

.poll .percent {
    font-size: 0.8em;
    margin-left: 10px;
}

.poll .progressbar {
    height: 10px;
    border: 1px solid #365925;
    width: 60%;
}

.poll .progressbar span {
    background-color: #87BF2C;
    width: 100%;
    height: 10px;
    display: block;
    font-size: 0;
    overflow: hidden;
}

.poll .progressbar span span {
    display: none;
}

.poll .total {
    color: #515151;
    margin-top: 0.5em;
    font-style: italic;
}

.poll .poss {
    color: #959595;
    font-style: italic;
}

.pun .poll_mess {
    font-weight: bold;
    text-align: center;
}

.pun .pollbut {
    font-size: 0.8em;
    padding: 7px 0 0 30px;
}

.pun .pollbut input {
    margin-right: 12px;
}

/* Growl messages */
#growlcontainer {
    position: fixed;
    right: 20px;
    top: 20px;
    width: 500px;
    font-size: 12px;
    z-index: 1000;
}

#growlcontainer p {
    font-family: Arial, sans-serif;
    margin: 0;
}

.growlstatus {
    position: relative;
    color: #000;
    min-height: 48px;
    /* icon height */
    padding: 10px;
    margin-bottom: 5px;
    background-color: #F1EDCA;
    border: 1px solid #888;
    opacity: 0.8;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
}

.growlstatus:hover {
    border-color: #777;
    opacity: 0.9;
}

.growlstatus .growltitle {}

.growlstatusclose {
    visibility: hidden;
    float: right;
    width: 16px;
    height: 16px;
    cursor: pointer;
    margin: 0 0 3px 5px;
}

.growlstatus:hover .growlstatusclose {
    display: block;
    visibility: visible;
}

.growlwithicon {
    padding-left: 68px;
    background-position: 10px 10px;
    background-repeat: no-repeat;
}

/* Spoiler_tag */
.spoiler_wrap {
    padding: 0px 5px !important;
    border: 1px solid #66686D !important;
    border-radius: 6px;
    background-color: rgba(114, 136, 217, 0.24);
    moz-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    -webkit-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
}

.spoiler_head {
    cursor: pointer;
    color: rgba(255, 255, 255, 0.9);
    font-weight: bold;
}

.spoiler_head span {
    font-weight: normal;
}

.spoiler_text {
    padding-bottom: 5px !important;
    display: none;
    color: rgba(255, 255, 255, 0.800);
    cursor: auto;
}

/* Emoticons */
#post .txtarea textarea {
    line-height: 1.4em;
    /* x20 */
}

#smilies_box {
    width: 100%;
    float: bottom;
    margin-top: 30px;
    text-align: center;
    overflow-x: hidden;
    overflow-y: auto;
    height: 120px;
    /*+border-radius: 6;*/
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    -khtml-border-radius: 6px;
    border-radius: 6px;
    background-color: rgba(0, 0, 0, 0.480);
    border: 1px solid #66686D;
    padding-top: 6px;
}

#rules-block {
    font-size: medium;
    padding: 5px;
}

#smilies_box ul {
    font-size: medium;
    height: auto;
}

#smilies_box li {
    display: inline;
    cursor: pointer;
}

.modlist {
    display: none;
}

input[type=text],
