    margin: 12px auto;
}

.pun span.bbs {
    text-decoration: line-through;
}

.pun .postmsg {
    font: normal medium / 1.75em verdana, arial, sans-serif;
}

.pun .postsignature p {
    max-height: 7.5em;
    line-height: 1.5em;
    padding: 0;
    overflow: hidden;
    text-align: center;
}

.pun .postleft .email a,
.pun .postleft .website a,
.pun .postleft .pw a {
