    overflow: hidden;
}

#cse-search-results iframe {
    /*+border-radius: 6;*/
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    -khtml-border-radius: 6px;
    border-radius: 6px;
    width: 90%;
    border: 1px solid #66686B;
}

#ansifoot {
    font-size: small;
    text-align: left;
    color: #FFFFFF;
}

/*****************************************************************
1. INITIAL SETTINGS
*****************************************************************/
/* Limited Reset
----------------------------------------------------------------*/
.pun table,
.pun div,
.pun form,
.pun p,
.pun h1,
.pun h2,
.pun h3,
.pun h4,
.pun h5,
.pun pre,
.pun blockquote,
.pun ul,
.pun ol,
.pun li,
.pun dl,
.pun dt,
.pun dd,
.pun th,
.pun td,
.pun fieldset,
.pun img,
.pun abbr,
.pun cite {
    margin: 0;
    padding: 0;
    border: 0;
}

.pun ul,
.pun ol {
    list-style: none;
}

/* Structural Settings
----------------------------------------------------------------*/
.pun .clearer,
.pun .nosize {
    height: 0;
    width: 0;
    line-height: 0;
    font-size: 0;
    overflow: hidden;
}

.pun .clearer,
.pun .clearb {
    clear: both;
}

.pun .nosize {
    position: absolute;
    left: -9999em;
    text-indent: -9999em;
    width: 0;
}

* html .inbox,
* html .inform,
* html .pun,
* html .tclcon,
* html .codebox {
    height: 1px;
}

.pun,
.pun .inbox,
.pun .inform,
.pun .tclcon,
.pun .codebox {}

.clearl {
    clear: left;
}

/* Hidden Elements
----------------------------------------------------------------*/
#brdfooter h2,
#brdstats h2,
#brdstats .conl dt,
#brdstats .conr dt,
#modcontrols dt,
#searchlinks dt,
div.postright h3,
.pun .required strong span {
    position: absolute;
    display: block;
    overflow: hidden;
    width: 0;
    left: -9999em;
    text-indent: -9999em;
}

/*****************************************************************
2. TEXT & CONTENT
*****************************************************************/
/* Text Defaults
----------------------------------------------------------------*/
.pun {
    font: 68.75% / 1.4545em Verdana, Helvetica, Arial, sans-serif;
}

.pun table,
.pun td,
.pun th,
.pun input,
.pun select,
.pun optgroup,
.pun textarea,
.pun small,
.pun samp,
.pun legend {
    font-size: medium;
    font-family: verdana, helvetica, arial, sans-serif;
}

.pun pre,
.pun code {
    font-size: medium;
    font-family: consolas, monaco, "bitstream vera sans mono", "courier new", courier, monospace;
}

.pun pre code {
    font-size: medium;
}

.pun strong {
    font-weight: bold;
    font-size: medium;
}

.pun em {
    font-style: italic;
}

.pun ins {
    color: #000;
}

/* Content Defaults
----------------------------------------------------------------*/
.pun p,
.pun ul,
.pun ol,
.pun dl {
    font-size: medium;
    padding: 3px 5px;
}

.pun h2 {
    font-size: x-large;
    border-radius: 6px 6px 0px 0px;
    background-color: rgba(114, 136, 217, 0.8);
    text-align: center;
    padding: 10px 6px;
    margin-top: 25px;
    moz-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    -webkit-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
}

.pun .blocktable h2 {
    color: #FFFFFF;
    font-weight: bold;
    background-color: rgba(114, 136, 217, 0.750);
    font-size: x-large;
    padding-bottom: 10px;
    padding-top: 10px;
}

.pun div[id^=idx] > h2 {
    cursor: pointer;
}

.pun h3 {
    font-size: 1.091em;
    padding: 3px 0;
}

.pun table p,
.pun table h3 {
    padding: 0;
}

.pun span.warntext,
.pun p.warntext {
    font-weight: bold;
}

/* User Content (Announcements, Rules, Posts)
----------------------------------------------------------------*/
.pun .usercontent p,
.pun .postmsg p {
    padding: 0.75em 0;
}

.pun .usercontent ul,
.pun .postmsg ul {
    padding: 0.75em 1em 0.75em 2.5em;
    list-style: disc;
}

.pun .usercontent ol,
.pun .postmsg ol {
    padding: 0.75em 1em 0.75em 2.5em;
    list-style: decimal;
}

.pun .usercontent ol.alpha,
.pun .postmsg ol.alpha {
    list-style: lower-alpha;
}

.pun .usercontent li ol,
.pun .usercontent li ul,
.pun .postmsg li ol,
.pun .postmsg li ul {
    padding: 0.25em 1em 0.75em 2.5em;
}

.pun .usercontent li p,
.pun .postmsg li p {
    padding: 0;
}

.pun .usercontent h1 {
    font-size: 1.4em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .usercontent h2 {
    font-size: 1.2em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .usercontent h3 {
    font-size: 1.1em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .usercontent h4,
.pun .usercontent h5,
.pun .usercontent h6 {
    font-size: 1em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .quotebox cite {
    font-weight: bold;
    font-style: normal;
    padding: 0.75em 0.75em 0 0.75em;
}

.pun span.bbu {
    text-decoration: underline;
}

.pun span.bbs {
    text-decoration: line-through;
}

.pun span.bbs,
.pun del {
    text-decoration: line-through;
}

.pun del {
    color: #555;
}

.pun .postmsg ins,
#punhelp samp ins {
    text-decoration: none;
}

.pun div.postmsg h5,
#punhelp h5 {
    font-size: 1.1em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

/*****************************************************************
3. COMMON STYLES
*****************************************************************/
/* Page Layout
----------------------------------------------------------------*/
html,
body {
    margin: 0;
    padding: 0;
}

.pun {
    margin: 12px 20px;
}

#punredirect,
#punmaint,
#puninstall,
#pundb_update {
    margin: 50px 20% 12px 20%;
}

/* Vertical Element Spacing
----------------------------------------------------------------*/
#brdheader {
    margin: 0 0 12px 0;
}

#announce,
#brdstats {
    margin: 1em 0 2em;
}

#announce-block {
    border: 1px solid #666;
}

.pun .blocktable,
.pun .block,
.pun .blockform,
.pun .block2col,
#postreview {
    margin-bottom: 12px;
}

#punindex .blocktable,
.pun .blockpost {
    margin-bottom: 6px;
}

#postreview .blockpost {
    margin-bottom: -1px;
}

.pun .block2col .blockform,
.pun .block2col .block {
    margin-bottom: 0px;
}

.pun .linkst,
.pun .linksb {
    margin-top: -12px;
}

.pun .postlinksb {
    margin-top: -6px;
}

/* External Borders
----------------------------------------------------------------*/
.pun .box {
    border-style: solid;
    border-width: 1px;
    background-color: rgba(114, 136, 217, 0.850);
}

#brdheader .box {
    border-radius: 6px;
    overflow: hidden;
}

/* Default Internal Spacing
----------------------------------------------------------------*/
.pun .block .inbox,
.pun .blockmenu .inbox {}

/*****************************************************************
4. COMMON BOARD ELEMENTS
*****************************************************************/
/* Board Header
----------------------------------------------------------------*/
#brdtitle h1 {
    font-size: 44px;
    font-weight: bold;
    line-height: 1em;
    padding: 5px 0 0;
    text-align: center;
}

#brddesc {
    padding: 3px 0 5px;
    text-align: center;
    font-size: small;
}

#brdmenu li {
    display: inline;
    margin-right: 12px;
}

#brdmenu a:link,
#brdmenu a:visited {
    text-decoration: none;
}

#brdmenu a:hover,
#brdmenu a:active {
    text-decoration: underline;
}

#brdwelcome .conl {
    text-align: left;
}

#brdwelcome .conr {
    float: right;
    text-align: right;
}

/* Breadcrumbs and Post Links
----------------------------------------------------------------*/
.pun .linkst {
    padding: 8px 6px 3px 6px;
}

.pun .linksb,
.pun .postlinksb {
    padding: 3px 6px 8px 6px;
}

.pun .crumbs {
    clear: both;
