/*****************************************************************
5. MAIN TABLES
*****************************************************************/
.pun table {
    width: 100%;
    border-collapse: collapse;
    border-spacing: 0;
    empty-cells: show;
}

.pun .blocktable table {
    table-layout: fixed;
}

.pun td,
