    display: block;
    padding: 0.75em 0 0 0;
}

.pun .quotebox blockquote {
    width: 100%;
    overflow: hidden;
}

.pun .codebox pre {
    overflow: auto;
    width: 100%;
    overflow-y: hidden;
}

* html .pun .codebox pre {
    padding-bottom: 10px;
}

