    margin-left: auto;
    margin-right: auto;
    max-width: 50%;
    vertical-align: middle;
    border-radius: 6px;
    margin-bottom: 12px;
    margin-top: 12px;
    box-shadow: 0 0 24px rgba(0, 0, 0, 0.350);
}

.pun .postmsg .postimg img {
    max-width: 100%;
    vertical-align: middle;
}

.pun .postmsg .postimg a:link img,
.pun .postmsg .postimg a:visited img {
    border-style: solid;
    border-width: 2px;
}

.pun .blockpost label {
    padding: 3px 6px;
    border-style: solid;
    border-width: 1px;
    vertical-align: middle;
    display: inline-block;
}

.pun .blockpost label * {
    vertical-align: middle;
    margin: 0;
    padding: 0;
