@-moz-document domain("terraconia.de"), domain("www.terraconia.de") {
/* ########## ALLGEMEIN ########## */
/* Hintergrund */
#main > div > div > .content {
    background-color: #1c1c1cbf;
}


#main > div > div > .sidebar {
    background-color: #1c1c1cbf;
}


/* Sidebar */
.sidebar > div > fieldset:not(:last-child), .sidebar > div > div:not(:last-child) {
    border-bottom: 1px solid #555555;
}


/* Shoutbox */
#shoutbox {
    background-color: #232323;
    border: solid 1px #555555 !important;
}


#shoutbox .shoutboxContent.asc:after {
    background: linear-gradient(to bottom,rgba(250,250,250,0) 0%,#232323 100%);
}


input[type='text'], input[type='search'], input[type='date'], input[type='datetime'], input[type='email'], input[type='number'], input[type='url'], input[type='password'], textarea, select[multiple] {
    background-color: #232323;
    border: solid 1px #555555 !important;
}


.container {
    background-color: #232323;
    border: 1px solid #555555;
}


/* ########## STARTSEITE ########## */
/* News */
li:nth-child(2n+1) .message .messageContent {
    background-color: #232323;
}
.message .messageContent {
    background-color: #232323;
}

.message {
    border: 1px solid #555555;
}


/* ########## FORUM ########## */
/* Foren */
.wbbBoardList .wbbBoardNode1 {
    background-color: #232323;
    border: solid 1px #555555 !important;
}
.wbbBoardList .wbbBoardNode2 {
    background-color: #232323;
    border: solid 1px #555555 !important;
}


.containerList > li:nth-child(even) {
    background-color: #232323;
}


/* Profil */
.tabMenu > ul {
    background-color: #232323;
    border-color: #555555;
}


.tabMenu > ul > li.ui-state-active > a {
    background-color: #1c1c1c;
    border-color: #555555 #555555 #555555;
}


    /* Eigene Antworten */
.commentList > li:nth-child(2n) .commentResponseList .commentResponse:nth-child(2n+1) {
    background-color: #232323;
}
.commentList > li:nth-child(2n+1) .commentResponseList .commentResponse:nth-child(2n+1) {
    background-color: #232323;
}


    /* Eigene Kommentare */

    /* Abtrennung Antworten */
.commentResponse {
    border-top: 1px solid #555555;
}


    /* Abtrennung Antworten & Antwort erstellen */
.commentOptionContainer.jsAddResponseActive, .commentResponseList + .commentOptionContainer {
    border-top: 1px solid #555555;
}


/* Beiträge */
    /* Siedebar */
.message {
    background-color: #1c1c1c;
}


    /* Abtrennung Sidebar & Beitrag */
.message.messageSidebarOrientationLeft .messageContent {
    border-left: 1px solid #555555;
}


    /* Abtrennung in Beiträgen */
.messageHeader + .messageBody > div:first-child, .messageBody > div:not(.messageFooter):not(:first-child) {
    border-top: 1px dotted #555555;
}


    /* Abtrennung in Sidebar */
.message.dividers .userCredits {
    border-top: 1px solid #555555;
}


    /* Footer */
.messageFooterNote {
    border-left: 2px solid #171717;
    background-image: -webkit-linear-gradient(90deg, rgba(221,221,221,0.2) 0%, rgba(221,221,221,0) 40%);
    background-image: linear-gradient(90deg, #00000033 0%, #ff0e0e00 40%);
}


/* Unterforen */
    /* Threads */
.table td, .cke_wysiwyg_div table td {
    background-color: #232323;
}
.table > tbody > tr:nth-child(even) > td, .cke_wysiwyg_div table > tbody > tr:nth-child(even) > td {
    background-color: #232323;
}


.table > tbody > tr:not(:last-child) > td, .cke_wysiwyg_div table > tbody > tr:not(:last-child) > td {
    border-bottom: 1px solid #5555554d;
}
.table > tbody > tr > td:not(:last-child), .cke_wysiwyg_div table > tbody > tr > td:not(:last-child) {
    border-right: 1px solid #5555554d;
}
}

