@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.nicovideo.jp") {
/*Userstyle for nicovideo.jp by Remiy.*/
/* https://userstyles.org/styles/141816/niconico-for-gecko-html5 */
/*Update: 2020.02.15*/

/*====================消すもの====================*/
#PAGEURGENT,/*動画の上に割り込むお知らせなど*/
.BottomContainer,/**/
.BottomContainer-main,/**/
.BottomContainer-side,/**/
.NicoSpotAdContainer,/*広告*/
.FacebookSDKLoader,/**/
.MiniBannerAdContainer,/*広告*/
.PlayerSkipNextButton,/*動画部分コントローラ､次の動画ボタン*/
.UadButtonBalloon,/**/
.SwitchToFlashLead-button/*Flash切り替えボタン*/
{ 
	display:none !important;
}

/*====================黒背景====================*/
input.CommentCommandInput,input.CommentCommandInput,/*コマンド入力欄*/
div.CommentInput,/*コメント入力欄*/
pan.CommentPanelDataGrid-headerCell,/*コメント一覧*/
span.CommentPanelDataGrid-cell,/*コメント一覧*/
div.CommentPanelContextMenuContainer-menuItem,/*コメント一覧右クリックメニュー*/
div.CommentPanelContextMenuContainer-menuHeading,/*コメント一覧右クリックメニュー*/
textarea.CommentInput-textarea,/*コメント入力欄*/
.CommentPanelContainer,/*コメント関係*/
.CommentPanelContainer .CommentPanelDataGrid,/*コメント関係*/
.CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell,/*コメント関係*/
.CommentPostContainer-commandInput,/*コメント関係*/
.CommentPostContainer-commentInput,/*コメント関係*/
.CommentInput,/*コメント関係*/
.ControllerContainer,/**/
.element,/**/
.react-grid-Container,/**/
.MainContainer-commentPanel,/**/
.MainContainer-playerPanel,/**/
.react-grid-HeaderCell,/**/
.WatchAppContainer,/*動画エリア外*/
.PlayerPanelContainer-tab,/*コメント欄上のタブ*/
.PlayerPanelContainer-content/*動画リスト*/
{
	background:#000 !important;
}

/*====================白文字====================*/
input.CommentCommandInput,input.CommentCommandInput,/*コマンド入力欄*/
div.CommentInput,/*コメント色*/
div.CommentPanelContextMenuContainer-menuItem,/*コメント一覧右クリックメニュー*/
div.CommentPanelContextMenuContainer-menuHeading,/*コメント一覧右クリックメニュー*/
span.CommentPanelDataGrid-headerCell,/*コメント一覧*/
span.CommentPanelDataGrid-cell,/*コメント一覧*/
textarea.CommentInput-textarea,/*コメント入力欄*/
.CommentPanel,/*コメント関係*/
.CommentPanelContainer .CommentPanelDataGrid,/*コメント関係*/
.CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell,/*コメント関係*/
.CommentPostContainer-commandInput,/*コメント関係*/
.CommentPostContainer-commentInput,/*コメント関係*/
.CommentInput,/*コメント関係*/
.react-grid-Main,/**/
.PlayerPanelContainer-tab,/*コメント欄上のタブ*/
.SeriesMediaObject-title,/*動画リスト*/
.VideoMediaObject-title,/*動画リスト*/
.NextPlayVideoContainer-title,/*動画リスト*/
.VideoDescriptionSeriesContainer-label/*シリーズの文字色*/
{
	color:#fff !important;
}

/*====================白背景黒文字====================*/
span.CommentPanelDataGrid-cell:hover,/*コマンドとコメントとコメント一覧*/
.react-grid-Cell:hover/**/
{
	background: #fff !important;
	color:#000 !important;	
}

/*====================その他色関係====================*/
/*右クリックメニュー*/
.CommentPanelContextMenuContainer-menuItem:hover{
    background: #eee !important;
    color: #000 !important;

}

/*コメントホバー時フォント色*/
.CommentPanelDataGrid-TableRow:hover{
    color:#000;
}

/*====================動画エリア====================*/
/*画面サイズ*/
.WatchAppContainer-main{
	width:100%;/*ここvwにするとスクロールバーの分ずれる*/
	height: 48vw;/*プレイヤー部分がおおよそ16:9になるように*/
}
.VideoContainer {
	position: relative;
	width: 82vw !important;
	height: calc(48vw - 37px)  !important;/*37pxはコメントバーの分*/
}

/*コントローラ、動画上に表示したいので*/
.ControllerContainer{
	position: absolute;
	z-index: 2;
	bottom: 38px;
	opacity: 0;
	-moz-transition: 0.8s;
	-webkit-transition: 0.8s;
}
.ControllerContainer:hover{
	opacity: 0.7;
}
.ControllerBoxContainer{
border-bottom:1px solid #eee !important;
	height: auto !important;
}

/*動画エリアに関係ある部分*/
body.is-autoResize .WatchAppContainer-main {
    width: auto !important;
    margin: 0px auto;
}
.CommentPanelContainer{
	bottom:0px !important;
}
.VideoDescriptionContainer{
	width:82vw !important;
	margin:16px;
}
.MainContainer-player {
	width: 82vw !important;
	height: 48vw;
}

/*プレ登録ボタン。コントローラを上にずらした為。*/
.PreVideoStartPremiumLinkContainer{
	bottom: 40px !important;
}

/*動画上の流れてくるやつ*/
.MarqueeContainer {
 	height: 0px !important;
}

/*急に現れた動画上の広告。*/
.MarqueeContainer .Marquee{
	z-index: 0 !important;
}

/*====================動画エリア右(コメント欄)====================*/
/*コメント欄のサイズとか*/
.MainContainer-commentPanel,.CommentPanelContainer,
.element,.react-grid-Container,.MainContainer-playerPanel{
	width:calc(100vw - 82vw - 17px) !important;/*17pxはスクロールバー､環境依存するかも*/
}

/*コメントログ*/
.DateTimeInput {
    display: inline;
    vertical-align: top;
}
.DateTimeInput .DateTimeInput-date {
    width: 40%;
}
.DateTimeInput .DateTimeInput-time {
    width: 25%;
}
.CommentPanelMenuContainer-pastCommentMenu {
    background: #000 none repeat scroll 0% 0% !important;
}

/*コメント欄上のアイコン｡*/
  .VideoMenuContainer-area .ActionButton,
	.VideoMenuContainer-areaLeft .ActionButton,
	.VideoMenuContainer-areaRight .ActionButton{
    width: 26px;
    height: 26px;
  }

/*コメント欄上のアイコンの背景*/
  .VideoMenuContainer-area,
  .VideoMenuContainer-areaLeft,
  .VideoMenuContainer-areaRight{
    background: #000 none repeat scroll 0% 0% !important;
}

/*シークバー*/
.SeekBarContainer {
    position: relative;
    padding-top: 0px;
    padding-left: 0px;
    padding-right: 0px;
    padding-bottom: 1px;
}

/*右クリックメニュー表示位置*/
/*.ContextMenu{
	left: auto !important;
	right: 20px !important;
}*/

/*ニコるのランク別背景色､いくつまであるのか不明｡*/
.CommentPanelContainer [data-row-nicoru-rank="1"]{
    background: #222;
}
.CommentPanelContainer [data-row-nicoru-rank="2"]{
    background: #333;
}
.CommentPanelContainer [data-row-nicoru-rank="3"]{
    background: #444;
}
.CommentPanelContainer [data-row-nicoru-rank="4"]{
    background: #555;
}
.CommentPanelContainer [data-row-nicoru-rank="5"]{
    background: #666;
}

/*====================動画エリア外====================*/
/*ヘッダ*/
.HeaderContainer {
	padding: 0;/*0にしないと上に謎スペースできる*/
}

/*動画タイトル*/
.VideoTitle{
	color: #eee;
	margin:-72px -72px -72px 0px;
}

/*動画情報*/
div.HeaderContainer-row{
	width:90% !important;
	position: absolute;
	top:51vw;
	margin:32px;
}

/*タグ*/
div.Grid.TagContainer{
	position: absolute;
	top:55vw;
	margin:32px;
}

/*動画説明*/
div.VideoDescriptionContainer{
	position: absolute;
	top:59vw;
	margin:32px;
}

/*タイトル上のシリーズの位置*/
div.SeriesBreadcrumbs{ 
	display:none !important;
}

/*フッタ*/
.FooterContainer{
	margin-top:60vw;/*動画情報広げた時にちゃんと見えるように広めです*/
}

/*Flash切り替え*/
.WatchAppContainer-switchToFlash{
	width:82vw;
	height:30px;
	line-height: 30px;
	position: absolute;
	z-index:2;
	opacity: 0;
	-moz-transition: 0.8s;
	-webkit-transition: 0.8s;	
}
.WatchAppContainer-switchToFlash:hover{
	opacity: 0;
}

/*====================その他====================*/
/*body*/
body{
	overflow-x:hidden;
    overflow-y: auto;
}

/*広告*/
.MiniBannerAd{
    height:0px;
}

/*====================なんで書いたか忘れた部分====================*/
.VideoDescriptionExpander .VideoDescriptionExpander-switchExpand {
    padding: 2px 16px;
    border-radius: 2px;
    color: #57f;
}

.VideoMediaObject-thumbnail{
    width:5vw;
}

.react-grid-HeaderRow{
    border-bottom:1px #fff solid;
}

.VideoDescription-html{
    color:#aaa !important;
}

.___header-table-base___RcIqV, .___header-table___2EekI {
	border-bottom: #fff 1px solid;
}

}

@-moz-document domain("roblox.com")
{
  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  #header
  {
    background-color: #f2babf;
  }

  .navigation .notification-icon,
.rbx-highlight
  {
    background: #f2babf;
  }

  #chat-header
  {
    background: #f2babf;
  }

  .slide-item-container-left,
.profile-avatar-right
  {
    background: #f2babf;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #f2babf inset;
 /* Uhm...what?*/
    ;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #cd86ab !important;
  }

  .rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs
  {
    background-color: #f2babf !important;
    border-color: #f2babf;
  }

  .rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover
  {
    background: #f2babf !important;
    border-color: #f2babf;
  }

  #TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor
  {
    display: none !important;
  }

  #navbar-search-input,
#navbar-search-btn
  {
    border: 1px solid #f2babf;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://i.pinimg.com/750x/f0/81/46/f0814644925cdc43782037c17d42024a.jpg") !important;
    background-size: fill !important;
    background-attachment: fixed !important;
  }

  #party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #f2babf, #cd86ab);
  }

  #ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: rgba(157, 162, 223, 1);
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(242, 186, 191, 1);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(205, 134, 171, 1);
  }
}

