    div.im3,
    div#contents.mag,
    div#wrapper,
    div#contentsHeader,
    div#headerSearch,
    div.listTextlink,
    div.listTextlink ul li,
    div.fdFt,
    li.listFeedWrap,
    div.epContents,
    ul.picList,
    article.anchorMod a,
    div.anchorMod a,
    div.relLink,
    div.categoryFtNav,
    nav.listNav,
    nav.contents, nav.contents section article, nav.contents section article ul,
    nav.contents section section ul li,
    div.pagenation,
    div.areaNav,
    header.labelSmall div,
    div.mainBox section div,
    p.toWebBtn a,
    div.hd p,
    div.commentsNav ul li span,
    div.commentsNav ul li,
    footer,
    footer div ul li a,
    footer a,
    div.footerInner, 
    div.buttonMedium p,
    div.newsFeedTab ul.newsFeedTab_list,
