@-moz-document url-prefix("https://www.roblox.com/games/") {
/*** MAIN CODE - Made by Freeplay - Don't make fun of me code >:( ***/

	body {
		overflow-x: hidden;
	}


	.dark-theme {
		background-color: black;
	}

	.dark-theme .content{
		background-color: black;
	}

@media only screen and (min-width: 992px) {
	.dark-theme .content {
		max-width: none;
		padding: 0px;
	}
	
	.page-content{
		max-width: none!important;
		width: 100%;
		margin: 0px;
		padding-left: 175px;
	}

	.leaderboard-abp{
		display: none;
	}
	.right-abp{
		background-color: #070707;
		width: 175px;
		height: 100%;
		margin: auto;
		padding-top: 10px;
		position: fixed;
	}

	.col-xs-12{
		height: 500px;
	}

	.container-footer{
		display: none;
	}


	.game-main-content .game-thumb-container{
		width: 100%;
		height: 520px;
		position: absolute;
		background-color: black;
	}
	.carousel{
		width: 100%;
		padding-left: 335px;
		height: 520px;
		right: 0;
	}
	.carousel:active {
		z-index: 100;
	}

	.carousel-inner {
		max-height: 100%;
		max-width: calc(100% - 500px);
		position: fixed;
		transition: background 2s;
	}
	.carousel-inner:active {
		padding-right: 175px;
		max-height: 520px;
	}
	.carousel-inner::after { /* Carouse shadow for background */
		background-image: linear-gradient(to top, #000, rgba(0,0,0,0));
		position: absolute;
		width: 100%;
		height: calc(25px + 15%);
		z-index: 3;
		display: flex;
		content: "";
		bottom: 0;
		pointer-events: none;
	}
	.carousel-inner:active:after { /* Carouse shadow for background */
		height: 0px;
	}

	.carousel-inner .item{
		height: 100%;
	}
	.carousel .carousel-thumb{
		min-height: 540px;
		object-fit: cover;
	}
	.carousel .carousel-thumb:active{
		object-fit: contain;
		display: block;
		width: 80%;
		margin: auto;
		max-height: 500px;
		z-index: 6;
	}


	.tab-content{
		background-image: radial-gradient(farthest-corner at 100% 40px, rgba(10, 10, 10, .9), black, #070707);
		padding: 20px;
		margin-bottom: 0px!important;
		margin-top: -5px;
		min-height: 700px;
	}

	/* Youtube Player */
	.carousel .flex-video embed, .carousel .flex-video iframe, .carousel .flex-video object{
		height: 510px;
		z-index: 5;
	}
	.carousel .flex-video.widescreen{
		padding-bottom: 100%; /* Fix youtube player cropping */
		margin-left: 100px;
		z-index: 5;
	}

	.carousel-control.left{
		left: 450px;
	}

	/* Details */
	.game-main-content .game-calls-to-action{
		position: absolute;
		z-index: 3;
		width: 450px;
		height: 540px;
		background-image: linear-gradient(to right, #070707, #050505, #050505, #000, rgba(0,0,0,0));
	}
	.game-title-container{
		margin-right: -500px;
	}
	.gotham-font h2{
		text-shadow: 0px 0px 10px black, 2px 2px 1px black, -2px -2px 1px black, -2px 2px 1px black, 0px -2px 1px black !important;
	}
	.gotham-font .game-main-content .game-calls-to-action, .gotham-font .game-main-content .game-calls-to-action .game-buttons-container{
		padding-top: 50px;
		padding-left: 30px;
	}
	.gotham-font .game-main-content .game-calls-to-action .game-buttons-container{
		padding-right: 90px;
		padding-bottom: 50px;
		z-index: 10;
	}
	.game-main-content.follow-button-enabled .favorite-follow-vote-share .social-media-share{
		position: absolute;
		margin-left: -330px;
		margin-bottom: 80px;
	}
	.social-share-container{
		text-align: left;
		left: 0;
	}
	
	/* social Links */
	.social-links {
		position: absolute;
		top: 0px;
		width: 400px;
		height: 0px;
		z-index: 4;
		margin-top: -360px;
		margin-left: 20px;
	}
	.social-links .medallion{
		padding: 0px;
		margin: 0px;
		height: 30px;
		border: none;
	}
	.social-links .contents{
		margin: 0px;
		height: 30px;
	}
	.social-links .title-wrapper{
		padding-left: 30px;
	}
	.type-img{
		background-size: 100% auto !important;
		width: 30px!important;
		height: 30px!important;
	}
	.col-md-4{
		width: 400px;
		height: 50px;
	}
	/* Fix Social Link Icons */
	.social-links .RobloxGroup {
		background-position: 0 65px;
	}
	.social-links .YouTube {
		background-position: 0 245px;
	}
	.social-links .Twitter{
		background-position: 0 215px;
	}
	
}
	/* Play Button */
	.game-main-content .game-play-buttons .btn-common-play-game-lg {
		min-width: 100%;
	}
	/* Tabs */
	.dark-theme .rbx-tabs-horizontal .nav-tabs{
		background-color: #000000b0;
		border-bottom: none;
	}

	.dark-theme .rbx-tab .rbx-tab-heading{
		background-color: #000000b0;
		transition: background-color .5s;
	}
	.page-content .rbx-tabs-horizontal{
		z-index: 6;
	}

	/* Badges + servers */
	.dark-theme .stack .stack-list .stack-row{
		background-color: rgba(33,33,33, 0.1);
		transition: background-color .5s;
	}
	.dark-theme .stack .stack-list .stack-row:hover{
		background-color: rgba(33,33,33, 0.3);
		transition: background-color .5s;
	}

	/* Notices */
	.dark-theme .container-empty, .dark-theme .section-content-off{
		background-color: rgba(33,33,33, 0.1);
	}


	/* Game Passes */
	.store-card{
		max-width: none;
		padding: 10px;
	}
	.store-card .store-card-name{
		text-align: center;
		padding-top: 10px;
		white-space: normal;
	}
	.dark-theme .store-card-price{
		padding-top: 10px;
		padding-bottom: 10px;
	}
	.dark-theme .store-card-price{
		justify-content: center;
		display: flex;
	}
	#rbx-passes-container .store-card{
		background-color: rgba(33,33,33, 0.1);
		transition: background-color .5s;
		border: none;
		border-radius: 20px;
	}
	#rbx-passes-container .store-card:hover{
		background-color: rgba(33,33,33, 0.3);
		transition: background-color .5s;
	}

/* RESPONSIVENESSSSSSSSSS */ 
@media only screen and (max-width: 1180px) {
	.page-content{
		padding-left: 0px !important;
	}
	.carousel-inner {
		max-width: calc(100% - 335px) !important;
	}
}
}

@-moz-document domain("jenkins.io") {
  /* Remove box and text shadows */
body * {
  box-shadow: none !important;
  text-shadow: none !important;
}

/* Dim Border */
.section-header, element, #page-body table.pane.bigtable, #main-panel table, #main-panel table tr td, .container-fluid.pane-frame, .manage-option:hover {
  border-color: #75715E;
}

html, body, #main-panel, #main-panel h1, #main-panel > div, #breadcrumbBar, #page-body, .Lozenge, .pane-header, .build-search-row, table.stripped tr:nth-child(2n),
table.stripped tr:nth-child(n), .pane-footer, .stage-start-time, .bottom-sticker-edge, .bottom-sticker-inner, .smallfont a, #buildQueue div
#side-panel div.pane-header, div.pane-header div a:link, .build-row-cell .pane.build-name .display-name, .bigtable,
.build-rss-links a, #description, div.pane-header a.collapse, .bigtable tr, .bigtable th, div.pane-header a:link, .alert .header, .alert .body, .alert .footer,
.cbwf-widget .alert-info, #hoverNotification, #tt, #tt div, .textarea-preview, .jenkins-config , .jenkins-config-widgets, .CodeMirror, .setting-input, .header,
.footer, .btn-decorator, .categories, #add-item-panel .item-copy {
  color: #E6DB74;
  background: #272822 !important;
}
.cbwf-info-action-popover.alert .caption, .cbwf-info-action-popover.alert .errors {
  width: max-content;
}

#main-panel > div > div[style*="background: #f8f8f8;"]{
  background: #272822 !important;
}
#plugins tr.plugin td.center.pane.uninstall form input.uninstall {
  background: #49483E;
  border: 1px solid #75715E !important;
}

.row .pane-content, .JTable-cell .actions-container::after, .bigtable a.model-link.inside, #executors a.model-link.inside, div.pane.build-details .model-link,
.build-row-cell .pane.build-name .build-link, .build-row, .build-controls {
  color: #E6DB74;
  background: none !important;
}

a:link, a b, #breadcrumbs a.inside.breadcrumbBarAnchor, td a:link,  #main-panel table tr td a, #main-panel table tr td a:link, select, .categories .label, div.pane-header,
#main-panel table tr td.setting-name, .JTable .JTable-row:first-child .JTable-cell-contents, #systemmessage div p strong, #buildHistoryPage .build-search-row .clear,
#executors div.pane-header > a {
  color: #E6DB74;
}
#pipeline-box h2, #main-panel h2, #rss-bar a, #main-panel table th a, table.sortable span.sortarrow, .section-header, .manage-option dl dt, h3, #main-panel .container-fluid a,
#main-panel h2 a, #add-item-panel .item-copy p  {
  color: #E6DB74 !important;
}

#main-panel {
  padding: 15px 15px 40px 25px;
}
#side-panel .task a, .pane, #side-panel .pane tr td, .yui-skin-sam .yuimenuitemlabel, .run-changeset-details .body .message, .glyphicon, .changeset-box .run-changeset,
.manage-option dl dd, .categories, #breadcrumb-menu .yuimenuitemlabel, .changeset-box.no-changes, #main-panel table tr td.setting-description, #main-panel table tr td {
  color: #75715E;
}
#systemmessage div:first-child {
  background: #3B3A32 !important;
}
#main-panel .pane-frame, .activity .activity-table, .multibranch-table, main article div.JTable.u-table-maxwidth, div.JTable {
  border-color: #75715E;
}
.JTable-cell-contents svg, .ColumnFilter.empty .Icon-filter svg, .Favorite path.Favorite-fill, .jenkins-pipeline-branches-list-action .pipeline-editor-link svg.svg-icon,
.ParametersRunButton .run-button-component.icon-button .run-button svg.svg-icon, .ParametersRunButton .run-button-component.icon-button .run-button svg.svg-icon:hover,
.history-button-component .history-button .svg-icon {
  fill: #75715E;
}
.ColumnFilter.empty input.autocomplete::placeholder {
  color: #75715E;
  opacity: 1; /* Firefox */
}
.JTable .JTable-row:first-child, #buildHistoryPage .build-search-row input {
  background: #3B3A32;
}
#filter-container > #filter-box {
  background-color: #3B3A32;  
}
.progress-spinner.not_built circle {
  stroke: #75715E;
}

/* Stage Logs */
.Stage.Logs .header {
  margin-left: 2px;
  margin-right: 2px;
}
.Stage.Logs .cbwf-dialog, .Stage.Logs .cbwf-widget pre {
  border: 1px solid #75715E !important;
}
.Stage.Logs .cbwf-dialog, .Stage.Logs .body {
  background: #3B3A32;
}
.Stage.Logs .node-name {
  /*  color: #75715E;*/
  background: #49483E;
}
.Stage.Logs .log-details {
  background: #49483E;
}
.Stage.Logs .cbwf-widget pre, .Stage.Logs .glyphicon-remove {
  color: #E6DB74;
}
.Stage.Logs .node-log-frame.SUCCESS, #plugins tr.plugin td.center.pane.enable {
  background: none !important;
}
.stage-failed-popover .alert, .stage-actions-popover .alert {
  color: #75715E !important;
}
.stage-failed-popover .footer {
  background: none !important;
  color: #75715E;
}


/* Text */
#main-panel, #main-panel h4, #main-panel p, .job-status- td, .dashboard td, .development-footer,
#pipeline-box .table-box tr.header th, #pipeline-box .table-box tr td .duration, .totals-box tr td div,
.stage-start-time, .parameters td, form[name=config] td, #l10n-footer a, #brewFooterTitle {
  color: #75715E !important;
}

#search-box, .tabBarFrame div.tabBar, .tabBarFrame div.tabBar a, .parameters .setting-main div select, select {
  background: #3E3D32 !important;
}
.tabBar {
  border: none !important;
}
input + label::after, input.cmn-toggle-yes-no + label::before, .yui-button button, .add-item-name input, #j-add-item-type-standalone-projects .j-item-options li:hover,
#j-add-item-type-nested-projects .j-item-options li:hover, #j-add-item-type-uncategorized .j-item-options li:hover {
  color: #E6DB74 !important;
  background: #49483E !important;
  border-color: #75715E !important;
}

/* Jobs table cell striping and text */
.jobsTable, .table-viewPort, .table-viewPort th {
  border: 1px solid #75715E !important;
}
.jobsTable td:nth-child(even), .jobsTable td:nth-child(2n) {
  background: #272822;
  border: 1px solid #75715E !important;
  color: #EBEBEB
}
.jobsTable td:nth-child(odd) {
  background: #3B3A32;
  border: 1px solid #75715E !important;
  color: #EBEBEB
}

/* Background and highlights for popup menus */
.ColumnFilter > div, .ColumnFilter > div > div, #search-box-completion > .yui-ac-content {
  border: 1px solid #75715E !important;
  color: #75715E !important;
}
.ColumnFilter > div:focus-within {
  border: none !important;
  width: 100%;
}
.ColumnFilter div div .item, #search-box-completion > .yui-ac-content, .yui-skin-sam .yuimenu .bd, #jenkins .yuimenu .bd, .token-list {
  background: #272822;
  color: #75715E !important;
  border-color: #75715E;
}
#search-box-completion > .yui-ac-content > .yui-ac-bd {
  padding-bottom: 2px;
  width: 100%;
}
#search-box-completion > .yui-ac-content > .yui-ac-bd li {
  border: 1px dotted #272822;
  margin: 2px 2px 2px 2px;
}
#hoverNotification, #tt div {
  border: 1px solid #75715E !important;
}

/* Grayed out color for jobs never built */
.job-status-nobuilt {
  background: #5e5e5e !important;
}

/* Remove white ouline around icons */
.svgResultStatus > circle {
  stroke: none !important;
}

#main-panel #view-message  #description > div > div > a {
  background: #AE81FF !important;
  color: #E6DB74 !important;
}
#pipeline-box .table-box tr.job td.stage-cell.ABORTED {
  /* Aborted build stage */
  background: #9da831 !important;
}

/* Remove left and right borders on some tables that have them. */
#page-body table.pane.bigtable, .bigtable tr {
  border-left-width: 0px;
  border-right-width: 0px !important;
}

input, textarea, #description, .dashboard, .bigtable .header *, .bigtable .header, .tabBar .tab.active a, .tabBar .addTab, .bigtable tr, .textarea-handle, .setting-input,
#buildHistoryPageNav.mouseOverSidePanelBuildHistory, #buildHistoryPageNav.mouseOverSidePanel, .alert, .healthReportDetails, #jenkins .jenkins-config table .CodeMirror-scroll,
table.bigtable tr.job-status-, .bigtable .job-status-red, .job-status-red-anime, .bigtable .job-status-blue, .parameters .setting-main div select {
	color: #E6DB74 !important;
  border-color: #75715E !important;
}
.textarea-handle {
  background: #75715E !important;
}

.Favorite .Favorite-fill, .jenkins-pipeline-branches-list-action .pipeline-editor-link .svg-icon, .ParametersRunButton .run-button-component.icon-button .run-button .svg-icon {
  fill: #BEBEBE;
}

/* Move the editor in front of the bottom sticker */
.editor {
  background: #B0B0B0;
  z-index: 1;
}

/* Left nav, top nav */
#header, #breadcrumbs, #side-panel, .pane-footer, .stage-start-time .date, .dashboard .tabBar, .tabBar .tab a {
  color: #75715E;
  background: #272822 !important;
  border-color: #75715E;
}
#side-panel {
  padding-bottom: 0px !important;
}
.left-bar {
  border: none !important;
}
.build-badge span {
  border-style: dotted !important;
  border-width: 2px !important;
  padding: 0px !important;
}

/* Hover highlight color */
.ColumnFilter > div > div > .item:hover, .parameters tbody:hover, .yuimenuitem:hover, .yuimenuitemlabel:hover, table.stripped tr:hover, table.stripped-even tr:hover,
table.stripped-odd tr:hover, #side-panel .task:hover, #side-panel .task:hover > .task-link, .task a:hover, #breadcrumbs li:hover, .JTable-row:hover, .mouseIsOverMenuSelector,
.bigtable .job-status-:hover, .bigtable .job-status-nobuilt:hover, .bigtable .job-status-red:hover, .bigtable .job-status-red-anime:hover, .bigtable .job-status-nobuilt-anime:hover,
.bigtable .job-status-blue:hover, .bigtable .job-status-blue-anime:hover, .bigtable .job-status-:hover a, .bigtable .job-status-aborted:hover, #people tr:hover, #people tr:hover a {
