    color: #87e8c0;
}
.focusmodeon #blockfocus,
.focusmodeon #blockfocus * { /* Focus Mode On */
    color: #87e8c0 !important;
}
.focusmodeon * { /* Focus Mode On, Out of Focus */
    color: rgba(135, 232, 192, .3);
}
#main a,
#main a:visited,
