@-moz-document domain(discordapp.com) {

	/* Disable time showing beside a message when hovering */
	.latin24CompactTimeStamp-2V7XIQ.timestampVisibleOnHover-2bQeI4.timestampCompact-29LLPQ.timestampBase-2p8O71.timestampCozyAlt-1Hs08c {
    	display: none;
	}

	/* Disable background darken when hovering a message */
	.mouse-mode .message-2qnXI6:hover {
    	background-color: rgba(0,0,0,0);
	}

	/* Disable background brighten when hovering a message with a mention */
	.mouse-mode .mentioned-xhSam7:hover {
    	background-color: rgba(250,166,26,0.05);
	}
  
}

