@-moz-document domain("music.163.com") {
/* fallback */
@font-face {
	font-family: 'Material Icons';
	font-style: normal;
	font-weight: 400;
	src: url(https://fonts.gstatic.com/s/materialicons/v48/flUhRq6tzZclQEJ-Vdg-IuiaDsNc.woff2) format('woff2');
}

/*Global*/
:root {
	--netease-red: #e80721;
	--black: #111;
	--gray1: #333;
	--gray2: #666;
	--radius: 4px;
}

*::before {
	font-family: 'Material Icons';
	font-size: 24px;
	line-height: 1;
	color: gray;
	display: block;
}

body {
	background: white;
	font-family: 'Noto Sans', 'Noto Sans CJK SC', sans-serif;
}

a:hover {
	text-decoration: none;
}

a:hover,
a:hover::before {
	color: var(--netease-red);
}

/*Navigation bar*/
.m-top {
	background: white;
	border: none;
}

.m-nav a {
	color: gray;
}

.m-nav a:hover,
.m-nav a.z-slt {
	background: none;
	color: var(--black);
}

.m-nav a.z-slt .cor,
.m-nav .hot,
.n-ban .download,
.g-sd1,
.m-playbar .updn .right,
