.m-tlist .icn-msg::before {
	content: 'notifications';
}

.m-tlist .icn-lv::before {
	content: 'star';
}

.m-tlist .icn-mbr::before {
	content: 'stars';
}

.m-tlist .icn-st::before {
	content: 'settings';
}

.m-tlist .icn-verify::before {
	content: 'verified_user';
}

.m-tlist .icn-ex::before {
	content: 'close';
}

.m-srch .srchbg {
	background: #e1e1e1;
}

.m-srch .srchbg::before {
	content: 'search';
	float: left;
	margin-left: 6px;
	margin-top: 4px;
}

.m-srch .parent label {
	color: gray;
}

.m-topvd {
	color: gray;
	border-color: gray;
	line-height: 30px;
	padding-left: 14px;
}

.m-topvd:hover {
	color: var(--gray1);
	border-color: var(--gray1);
}

/*Sub navigation*/
.m-subnav {
	background: white;
	border: none;
}

.m-subnav .nav a {
	color: var(--netease-red);
}

.m-subnav .nav a em {
	height: 21px;
}

.m-subnav .nav a:hover em,
.m-subnav .nav a.z-slt em {
	background: var(--netease-red);
}

/*Banner*/
.n-ban .wrap,
.n-ban .ban {
	width: 732px;
}

.n-ban .ban .btnl {
	left: -64px;
}

.n-ban .ban .btnr {
	right: -64px;
}

.n-ban .dots .pg {
	background: white;
	width: 6px;
	height: 6px;
	margin: 7px;
	border-radius: 3px;
}

