data.css:
@-moz-document domain("www.ozbargain.com.au") {
/* latin-ext */
@font-face {
  font-family: 'Raleway';
  font-style: normal;
  font-weight: 400;
  src: local('Raleway'), local('Raleway-Regular'), url(https://fonts.gstatic.com/s/raleway/v14/1Ptug8zYS_SKggPNyCMIT5lu.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Raleway';
  font-style: normal;
  font-weight: 400;
  src: local('Raleway'), local('Raleway-Regular'), url(https://fonts.gstatic.com/s/raleway/v14/1Ptug8zYS_SKggPNyC0ITw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
body {
 font-family: Raleway !important
}
/*background and foreground colors*/
 body, p, h1, h2, h3, h4 {
    line-height: 1.4em !important
}
ul.ng2-deals li div.ng2-dealimg > a div.n-vote, ul.ng2-deals li, div.menupopup, div.node.feature {
    background-color: black !important;
    border: none !important;
    box-shadow: 0 .3em 3em rgba(0, 0, 0, 0.5)
}
.tabs + .node-ozbdeal .content {
    color: #fff !important
}
ul.ng2 {
    background-color: #1f1f1f
}
.site-logo {
    background-size: auto 100%;
    height: 41px;
    width: 43px;
    margin-right: 5px;
    overflow: hidden;
}
#title {
    font-size: 2em;
}
h1#title, div.node h2.title {
    font-weight: 200;
}
div.wrap {
    min-width: 0 !important;
   	background: #000000; 
	background-color:  #1f1f1f !important;
    border-bottom: 3px double grey;
}
