    background-color: #161616;
    color: #d4e3f7;
    font-weight: 300;
}
/*divider color*/
 form.search input[type="text"], div.foxshot-container a, div.couponcode, div.sidebar ul.ozblist > li, div.node-teaser-unpublished, div.node-teaser, div.menupopup, div.menupopup div.group, #forum td, #forum th, #forum table, table.data, table.data td, table.data th, fieldset.collapsible legend, table tr > td, div.nodegrid div.node, div.menupopup h3, div.noti-item, div.nodegrid, div.couponcode strong, fieldset, div.ac-result > ul > li.searchac, dl.search-results dt.title, #s-control, div.ac-result, div.ac-result, div.userprofile div.activities > div, div.forumnew form.search, .btn, h2.section {
    border-radius: 0;
    border-color: #404449 !important;
}
ul.ng2-deals li, ul.ng2-deals, div.ac-result > ul > li.section, div.productlist div.img {
    background-color: transparent;
}
