@-moz-document domain("lyricstraining.com") {
:root {
  --max-width: 90vw; /* Max width game block */
  --width: 1253px; /* Width game block */
  --height: 100vh; /* Height game block */
  --bottom-height: 30vh; /* Bottom height lyrics */
  --time: 0.3s; /* Top bar transition time */
  --opacity: .1; /* Menu hide opacity */
  --debug: 0; /* Debug */
}

::-webkit-scrollbar {
    background: #004878;
    width: 10px;
}

::-webkit-scrollbar-thumb {
    background: #2c60a2;
    border-radius: 1em;
    box-shadow: inset 0px 0px 4px 1px rgba(0, 43, 80, 0.6);
}

.page-frame {
/*     min-width: 0%; */
    width: var(--width);
    max-width: var(--max-width);
}

#game-block {
    min-height: var(--height);
    height:     var(--height);
}

#video-block {
    bottom: var(--bottom-height);
}

#game-area.slots #video-frame {
    width: initial;
    margin-right: 200px;
}

#video-progress-bar {
    bottom: var(--bottom-height);
    transform: translateY(4px);
}

#lyrics-view, #lyrics-overlay {
    height: var(--bottom-height);
}

#page-head {
    position: fixed; /* absolute */
    transform: translateY(-100%);
    transition: opacity var(--time) linear, transform var(--time) linear;
    opacity: var(--opacity);
}

#page-head:hover {
    opacity: 1;
    transform: translateY(0px);
}


#header::after {
    transition: opacity var(--time) linear, transform var(--time) linear;
    position: absolute;
    font-family: 'lt-glyphs';
    content: "\e007";
    font-size: 50px;
    background-color: #f2f2f2;
    z-index: -1;
    opacity: 1;
}

#header:hover::after {
    opacity: 0;
    transform: translateY(-100%);
}


#page-head::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 60%;
    transition: height var(--time) linear;
    /* for testing */
    opacity: var(--debug);
    background-color: rgba(0, 200, 0, 30);
}

#page-head:hover::after {
    height: 40%;
}

#page-head.fixed {
    opacity: 1;
    transform: translateY(0);
}

#page-head.fixed::after, #page-head.fixed:hover::after {
    height: 0;
}

#game-area.slots #slots-view {
    display: flex;
}

#slots-view {
    flex-direction: column;
    width: 200px;
}

#slots-view>.slot {
    flex: 1;
    border-radius: 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

#slots-view>.slot>b {
    position: initial;
    height: initial;
    line-height: initial;
}

#lyrics-view>ul.lines {
    transform: translateY(2em);
}
}

