    padding-top: 1px;
    padding-bottom: 5px;
}

.DocumentItem-icon, .BookmarkItem-icon {
    font-size: 20px;
    line-height: 16px;
}


/********** SEARCH AND FILTER ************/

.DocumentTools-icon {
    color: #353535;
    transition: 0.3s;
}


/*********** LINES **************/
.Node-children {
    border-left: 1px solid #292929;
}

