    color: var(--wit);
    background-color: var(--grijs3); }

.button .icon, .button-secondary {
    color: var(--zwart); }

.panel .inputbox {
    background-color: var(--wit3); }

dd label {
    color: var(--wit3); }

dl.details dd {
    color: var(--wit3); }

dl.mini dt {
    color: var(--wit); }

.navigation .active-subsection a {
    background-color: var(--wit3); }

.navigation a {
    background-color: var(--wit3); }

.tabs .tab > a {
    color: var(--zwart);
    background-color: var(--wit3); }

.fancy_panel {
    background-color: var(--grijs); }
}

