/*-----------Animations----------*/
@keyframes open{
	0%	{max-height:0px;}
	100%{max-height:400px;}
}
@keyframes opacity{
	0%	{opacity: 0.0;}
	25%	{opacity: 0.25;}
	50% {opacity: 0.50;}
	75%	{opacity: 0.75;}
	100%{opacity: 1.0;}
}
@keyframes alpha_out{
	0%	{background: rgba(39, 44, 53, 255);}
	25%	{background: rgba(39, 44, 53, 191);}
	50% {background: rgba(39, 44, 53, 128);}
	75%	{background: rgba(39, 44, 53, 64);}
	100%{background: rgba(39, 44, 53, 0);}
}
@keyframes alpha_in{
	0%	{background: rgba(39, 44, 53, 0);}
	25%	{background: rgba(39, 44, 53, 64);}
	50% {background: rgba(39, 44, 53, 128);}
	75%	{background: rgba(39, 44, 53, 191);}
	100%{background: rgba(39, 44, 53, 255);}
}
@keyframes display{
	0%	{display:none !important;}
	100%{display:inline !important;}
}
._wD:hover{
	animation-name: alpha_in !important;
	animation-duration: 0.2s !important;
	animation-timing-function: ease-out !important;
	animation-fill-mode: forwards !important;
}
._wD{
	border-radius: 4px !important;
	animation-name: alpha_out !important;
	animation-duration: 0.2s !important;
	animation-timing-function: ease-out !important;
}	


/*-----------------------Search Bar-----------------------------*/
.sbtc{
	opacity:0.0;
	animation-name: opacity !important;
	animation-timing-function: ease-in-out !important;
	animation-duration: 0.3s !important;
	animation-delay: 0.3s !important;
	animation-fill-mode: forwards !important;
}
.sbsb_c{
	opacity:0.0;
	animation-name:opacity !important;
	animation-timing-function:linear !important;
	animation-duration: 0.05s !important;
	animation-delay:0.30s !important;
	animation-fill-mode: forwards !important;
}
#sfdiv, .lst-c {
    border-radius: 50px !important;
    opacity: 0.85;
}
.vasq {
	border-radius: 50px !important;
}
	
/*-------------------Suggestions Field-------------------------*/
