@-moz-document domain("duelingnexus.com")
{
html
  {
    background: url(https://i.imgur.com/SXZltT9.jpg) 0 0vw/cover fixed
  }

  div
  {
    /* */;
  }

  body
  {
    font-size: 12px;
    line-height: 1;
    color: #FFF;
    background-color: #c5202600;
    background-repeat: no-repeat;
  }

  hr
  {
    margin-top: 0px;
    margin-bottom: 0px;
    border: 0;
    border-top: 0px solid #eee;
  }

  .h2,
h2
  {
    font-size: 0px;
  }
}

@-moz-document url("https://www.comdotgame.com/"), url-prefix("https://www.comdotgame.com/"),  domain("comdotgame.com/") {
/* Req Dark theme for https://www.comdotgame.com/  */


body {
    background-color: #0c0b0c;
    font-size: 15px;
}

 main {
    display: block;
    background: #3f4040;
    padding: 10px 0;
    border-bottom: 1px solid #e5e7e9;
}

a {
    text-decoration: none;
    color: #97d5ff;
   
}

p  {
    text-decoration: none;
    color: #fff;
 
}



ul.games li.game .thumb {
    background: #959595 no-repeat center center;
    padding: 5px;
    width: 128px;
    height: 96px;
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
}

/* Background Color behind Thumbnails */
ul.games li.game .thumb, ul.games li.game .title {
    display: block;
    background-color: #333;
}


/* image thumbnail text colors etc content welcome  */
ul.games li.game a {
    display: block;
    text-decoration: none;
    background: 0 0;
    white-space: nowrap;
    width: inherit;
    height: inherit;
    color: #fff;
}


main .style {
    height: 16px;
    line-height: 16px;
    padding: 2px 0;
    color: #000 !important;
    background: #000 !important;
    background-color: #000 !important;
}


 main .div .style {
    display: block;
    background-color: #ff0000 !important;
               background: #ff0000 !important;
    padding: 10px 0;
    border-bottom: 1px solid #e5e7e9;
}

ul.comments .head {
    height: 16px;
    line-height: 16px;
    padding: 2px 0;
    color: #fff !important;
   
}


/* dashed line colors between new comments  */
ul.comments .head, ul.scores .head, head.stye{
    height: 16px;
    line-height: 16px;
    padding: 2px 0;
    color: #61b9ff !important;
    background-color: #3f4040 !important;
    border-bottom: 1px dashed #fff;
     color: #61b9ff !important;
}


game-comments {
    color: #6bbddc !important;
    Background-color: #ff0000 !important;;
}

 



content {
    background: #c3b4b4 !important;
   background-color: #ff0000 !important;
    border-bottom-right-radius: 5px;
    padding: 40px 10px 10px;
}

/* Search Bar */

main .search input {
    width: 400px;
    height: 30px;
    padding-left: 20px;
    padding-right: 20px;
    border: 1px solid #5f6161; 
    border-radius: 0px;
font-weight: bold;
    background-color: #565858;
    color: #fff;
    /* padding: 1px 2px; */
}


button {
    -webkit-appearance: button;
    -webkit-writing-mode: horizontal-tb !important;
    text-rendering: auto;
    color: #fff;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: center;
    align-items: flex-start;
    cursor: default;
    background-color: #55acee;
    box-sizing: border-box;
    margin: 0em;
 font-style: normal;
    height: 36px;
   min-width: 100px;
    margin-left: 5px;
    border-width: 1px;
    border-style: none;
    border-color: #55acee;
    border-image: initial;
}


.forum .topics a {
    display: block;
    color: #fff;
    background: 0 0;
}


}

