/*[[tc]]*/

h1,
h2 {
    color: var(--nav-text-color) !important;
}
a {
    color: var(--anchor-color-a) !important;
}
a:hover {
    color: var(--anchor-hover-color-a) !important;
}
a:focus,
a:active,
a:visited {
    color: var(--color-anchor-focus-active-visited-a) !important;
}
a:focus:hover,
a:active:hover,
a:visited:hover {
    color: var(--color-anchor-focus-active-visited-hover-a) !important;
}
body {
    background: var(--background-color) var(--background-image) !important;
    background-position: center top !important;
    background-attachment: fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    color: var(--body-text-color) !important;
}
.main_wrapper {
    background: var(--wrapper-color) !important;
    padding-left: 25px !important;
    padding-bottom: 55px !important;
    border-bottom-left-radius: 8px !important;
    border-bottom-right-radius: 8px !important;
}
table.mainframe:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2),
table.mainframe:nth-child(3) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) {
    background: transparent !important;
}
/* <- Buttons Start -> */
.btn,
[type="submit"] {
    font-weight: bold;
    cursor: pointer;
    background: var(--button-background-color) !important;
    height: 40px;
    color: var(--button-text-color) !important;
    font-size: 12pt;
    border: none;
    border-radius: 4px !important;
    padding-left: 15px !important;
    padding-right: 15px !important;
    text-align: center !important;
    -webkit-transition: var(--trans-speed-on-1) !important;
    -moz-transition: var(--trans-speed-on-1) !important;
    -o-transition: var(--trans-speed-on-1) !important;
    transition: var(--trans-speed-on-1) !important;
}
.btn:hover,
[type="submit"]:hover {
    background: var(--button-background-hover-color) !important;
    color: var(--button-text-hover-color);
}

/* <- Buttons End -> */
/* <- Input / Select / Checkbox Start -> */
textarea,
input,
#descr {
    background-color: var(--input-select-background-color) !important;
    border: 1px solid var(--border-color) !important;
    border-radius: 4px !important;
    color: var(--input-select-text-color) !important;
    padding: 10px !important;
}
select {
    background-color: var(--input-select-background-color) !important;
    border: 1px solid var(--border-color) !important;
    border-radius: 4px !important;
    color: var(--input-select-text-color) !important;
    padding: 10px !important;
}
input[type="checkbox"] {
    border: 1px solid var(--border-color) !important;
    border-radius: 4px !important;
}
td.rowhead {
    border-right: 1px solid var(--border-color) !important;
}
fieldset {
    background: var(--box-background-color) !important;
    border-color: var(--border-color) !important;
}
/* <- Input / Select / Checkbox End -> */
.l_side,
.r_side {
    background: transparent !important;
}
[src="https://www.funfile.org/themes/Autumn/images/blank.gif"] {
    height: 176px !important;
}
.head_banner {
    background: transparent var(--logo) !important;
    background-repeat: no-repeat !important;
    background-size: 841px 176px !important;
    width: 841px !important;
    height: 176px !important;
}
[src="/pic/rssicon.png"],
[src="/themes/Autumn/images/rssicon.png"] {
    background: var(--rss) !important;
    background-size: 74px 74px !important;
    background-repeat: no-repeat !important;
    width: 74px !important;
    height: 74px !important;
    box-sizing: border-box !important;
    padding-left: 74px !important;
    border-radius: 4px !important;
    position: relative !important;
    top: 40px !important;
    left: 0px !important;
