  html ,#game-rps-container
  {
    background: url(https://cdn.discordapp.com/attachments/668561975219060766/673372318235361329/image0.jpg)!important;
  }

  .game-field-zone-activate
  {
    content: url("https://cdn.discordapp.com/attachments/668561975219060766/673372318235361329/image0.jpg");
  }

  *
  {
    border-radius: 0px 10px 10px 10px;
  }

  .game-field-card
  {
    border-radius: 0;
  }

  .game-field-zone
  {
    border-radius: 0;
  }

  #editor-main-deck, #editor-extra-deck, #editor-side-deck
  {
    border: 1px solid #ffffff47;
  }

  #navbar-left , #navbar-right
  {
    position: fixed;
    z-index: 5;
  }

  #navbar-middle
  {
    position: fixed;
    padding-left: 35%;
    top: 0%;
  }

  .game-selection-card
  {
    margin: 1vh 1vw 1vh 1vw;
    z-index: 7;
  }

  .game-field-card
  {
    z-index: 10;
  }

  .game-field-stats-text
  {
    color: #fff !important;
    z-index: 3;
  }

  .game-field-zone-content
  {
    color: #fff;
    z-index: 5;
  }

  .game-card-negated
  {
    z-index: 5;
  }

  .game-field-zone-activate
  {
    z-index: 8;
    position: absolute;
  }

  #game-option-list
  {
    padding: 0.5vw;
  }

  #game-option-list .engine-button
  {
    margin: 0.25vw;
    padding: 0;
    position: relative;
    bottom: 0;
  }

  #game-tooltip
  {
    color: white;
    font-size: 1.05vw;
    background: radial-gradient(#00000070, #0a0202);
    border: 1px dashed #000;
    padding: 1vh;
    z-index: 7;
    position: fixed;
  }

  #downloadDeck
  {
    background-color: #19660c;
  }

  #card-description-box
  {
    background: url("https://imgur.com/XUYfjMk.png");
  }

  #game-alert-window,
#options-window,
#game-results-container,
#game-message-window,
#game-waiting-menu,
#game-selection-window,
#game-yesno-window,
#game-position-window,
#game-option-window,
#game-announce-window,
#game-announce-card-window
  {
    background-color: #00000080;
    border-color: #fff;
    border-width: 15px 1px 1px 3px;
    border-radius: 15px;
    left: -15vw;
    top: 40vh;
  }

  #game-end-window
  {
    background-color: #00000080;
    border-color: #fff;
    border-width: 15px 1px 1px 3px;
    border-radius: 15px;
  }

  #game-room-container
  {
    width: 60vw;
    min-width: 10px;
    padding-bottom: 0px;
    padding-top: 0px;
    padding-left: 0px;
    padding-right: 0px;
  }

  #game-room-players
  {
    width: 40vw;
    min-width: 28px;
    padding: 2vh 2vw 2vh 1vw;
    margin-top: 20px;
  }

  #game-room-information
  {
    width: 16vw;
    min-width: 260px;
    padding: 2vh 2vw 2vh 1vw;
  }

  #game-selection-window
  {
    overflow-y: scroll;
    height: 25vw;
    width: 40vw;
    z-index: 6;
    left: 15vw;
    top: 30vh;
  }

  #game-deck-selection
  {
    border: solid #fff;
    border-width: 12px 1px 1px 3px;
    border-radius: 11px;
    overflow-y: scroll;
    height: 300px;
    width: 450px;
    min-width: 120px;
    margin-top: 7px;
    padding: 5px;
    z-index: 2;
    position: absolute;
  }

  #options-area
  {
    position: fixed;
    right: 0.8vw;
    bottom: 25qpx;
  }

  #options-window
  {
    position: fixed;
    height: 200px;
    width: auto;
    overflow-y: visible;
    z-index: 10;
    left: 65vw;
    top: 60vh;
  }

  #game-siding-column
  {
    height: 37.67vw;
    width: 33.33vw;
  }

  #game-siding-column-2
  {
    border: 1px;
    height: 37.67vw;
    width: 37.67vw;
  }

  #game-siding-done
  {
    height: 4vw;
    width: 100%;
    position: relative;
    top: 5vw;
  }

  .game-field-zone
  {
    background: repeating-radial-gradient( #0000007d , #000000a6 );
    border: 3px solid #fff0;
    border-style: outset;
  }

  #my-profile , #home-area ,
#duel-area , #decks-area
, #gamelist-area
  {
    background-color: #010101b5;
  }

  #news-area
  {
    background: #000000b5;
  }

  .adsbygoogle
  {
    opacity: 0;
  }

  body
  {
    font-size: 14px;
  }
}

@-moz-document domain("duelingnexus.com")
{
/*delete this code section if you are having problems with the 3D game field */

  #game-selection-window
  {
    transform: translate(-19vw , -11vh);
  }

  #game-yesno-window
  {
    transform: translate(-4vw);
  }

  #game-container
  {
    perspective: 2100px;
  }

  #game-column
  {
    transform: rotatex(45deg) translatex(-20px) scale(0.9,1.02);
  }

  #game-action-menu
  {
    transform: translate(-27.5vw, -3vh);
  }

  #game-tooltip
  {
    transform: translatex(-27vw);
  }

  .game-avatar-area
  {
    transform: scale(1.15);
  }

  #game-rps-container , #game-rps-result
  {
    perspective: 1700px;
  }

  #game-rps-scissors
  {
    transform: rotatex(50deg) scaley(1.3);
    padding: 5px;
  }

  #game-rps-paper
  {
    transform: rotatex(50deg) scaley(1.3);
    padding: 5px;
  }

  #game-rps-rock
  {
    transform: rotatex(50deg) scaley(1.3);
    padding: 5px;
  }
}

@-moz-document url-prefix("http://example.com")
{
  #game-announce-window
  {
    overflow-y: scroll;
    z-index: 6;
    height: 20vw;
    width: 20vw;
  }
}

/*requesting full size of the images*/
img {
    min-width: auto !important;
    width: auto !important;
    height: auto !important;
    min-height: auto !important;
}

/*centering photosets*/
/* !!!please be aware that these containers are in the order they are nested, if you are planning to alter something!!! */
.post_full.is_photoset .photoset .photoset_row {
    display: inline-flex!important;
    align-items: center !important;
    margin: 0px !important;
    height: auto !important;
    justify-content: center !important;
}


.photoset,
.post_full.is_photo .photoset .photoset_row {
    display: inline-flex !important;
    margin: 0px !important;
    flex-direction: column !important;
    justify-content: center !important;
}


.post_full.is_photoset .photoset .photoset_row_3 .photoset_photo img,
.post_full.is_photoset .photoset .photoset_row_2 .photoset_photo img,
.post_full.is_photoset .photoset .photoset_row_1 .photoset_photo img,
figure.tmblr-full {
    display: inline-flex !important;
    align-content: center !important;
    margin: 0.5px !important;
    min-height: auto !important;
}



/*ensuring bigger images dont go outside boundaries*/
.post_full.is_photoset .photoset .photoset_row .photoset_photo img,
.post_full.no_body .post_media img,
.post_media_photo img,
.post_media_photo image,
.post_full.is_photo .post_media .post_media_photo,
.post_full.is_photo .post_media .post_media_photo_anchor {
    min-width: auto !important;
    width: auto !important;
    max-width: 540px !important;
}

.post_full.is_photoset .photoset .photoset_row_2 .photoset_photo img,
.photoset_row photoset_row_2 {
    min-width: auto !important;
    width: auto !important;
    max-width: 268px !important;
}

.post_full.is_photoset .photoset .photoset_row_3 .photoset_photo img,
.photoset_row photoset_row_3 {
    min-width: auto !important;
    width: auto !important;
    max-width: 177px !important;
}

/* necessary resizing for icons, thumbnails and such */
.reblog-list-item .reblog-avatar,
.reblog-list-item .reblog-avatar .reblog-avatar-image-thumb,
.post-activity-note-avatar .post-activity-avatar-link img,
.blog-selector-container .selected-blog .avatar {
    width: 25px !important;
    height: 25px !important;
}

/* xkit fixes start */

.xkit-people-notifier-person .people-notifier-avatar{
    width: 16px !important;
    height: 16px !important;
}

.notification .avatar_frame img,
.notification .banner_avatar_frame img,
.notes .note .avatar_frame img,
.ui_avatar .frame img,
.notification .notification_right img,
#xkit-extensions-panel-left .xkit-extension .icon,
.xkit-gallery-extension .icon img{
    width: 30px !important;
    height: 30px !important;
}

/* xkit fixes end */
.blog-list-item .blog-list-item-avatar-image,
.radar .radar_avatar img {
    width: 37px !important;
    height: 37px !important;
}

.post-composer_note-post .avatar img,
.post_full.is_note .avatar img,
.popover--tumblelog-select-dropdown .ts-avatar,
.peepr-body .indash_blog .post_chrome.answer .post_content .avatar img {
    width: 40px !important;
    height: 40px !important;
}

.native-audio-container .audio-image img {
    width: 85px !important;
    height: 85px !important;
}

.radar .radar_content .thumbnail_anchor img {
    width: 300px !important;
    height: 255px !important;
}

@-moz-document url-prefix("https://aur.archlinux.org/"), url-prefix("https://www.archlinux.org/") {
/** ArchLinux.org Dark
 * 
 * v0.22
 * By nakh
 */

/*** Custom Colors ***/
:root {
    --light: #ccc;
    --lighter: #e8e8e8;
    --dark: #03344d;
    --darker: brown;
    --code-highlight: #103740;
    
    --dark-bg: #2b2b2b;
}

/*** General Styles ***/
body {
    background-color: var(--dark-bg) !important;
}

a:link,
a:visited {
    color: #4ca6d3 !important;
}

select {
    color: var(--lighter) !important;
    background-color: var(--dark-bg) !important;
}

/*** Main Page ***/
#nav-sidebar > h4 {
    color: var(--lighter) !important;
}

#news h3 a {
    color: var(--lighter) !important;
}

/* Change the color of the padding around certain code blocks */
pre {
    background: var(--code-highlight) !important;
}

dl dt {
    color: var(--light) !important;
}

/*** Packages List ***/
#pkglist-search {
    background-color: var(--dark-bg) !important;
}

#pkglist-results {
    background-color: var(--dark-bg) !important;
}

#pkglist-results-form th,
#pkglist-results-form tr {
    background-color: var(--dark-bg) !important;
}

#pkglist-results-form tr:hover {
    background-color: var(--dark) !important;
}

#pkglist-about {
    background-color: var(--dark-bg) !important;
}

table.results {
    background-color: var(--dark-bg) !important;
}

/* Exact Matches box */
#exact-matches {
    background-color: var(--dark-bg) !important;
}

#exact-matches tr, #exact-matches th {
    background-color: var(--dark-bg) !important;
}

/*** Package Page ***/
#pkgdetails {
    background-color: var(--dark-bg) !important;
}

#actionlist {
    background-color: var(--dark) !important;
}

#actionlist h4 {
    color: var(--light);
}

/*** AUR ***/
#intro {
    background-color: #1a1a1a !important;
}

#news {
    color: var(--light) !important;
}

div.box {
    color: var(--lighter) !important;
}

#pkgsearch {
    background: var(--dark) !important;
}

#pkg-updates {
    background: #1a1a1a !important
}

#pkg-stats {
    background: var(--dark) !important;
}

#footer  {
    color: #f4f4f4 !important;
}

code {
    background: var(--code-highlight) !important;
}

.package-comments {
    color: var(--light) !important;
}

/*** TODO Pages. No, not things I will finish later. These are the actual WIPs in /todo/ ***/
#dev-todo-details {
    background-color: var(--dark-bg) !important;
}

.filter-criteria {
    background-color: var(--dark-bg) !important;
}

#dev-todo-pkglist tr,
#dev-todo-pkglist th {
    background-color: var(--dark-bg) !important;
}

#dev-todo-pkglist tr:hover {
    background-color: var(--dark) !important;
}

/* color: var(--light) !important */
}

@-moz-document domain(lobste.rs) {
    body {
        background-color: rgba(40, 44, 52, 1);
        color: rgba(255, 255, 255, 0.7);
    }

    a.domain {
        color: rgba(198, 120, 221, 0.8);
    }

    a.u-url {
        color: rgba(209, 154, 102, 1);
    }

    .byline>span[title] {
        color: rgba(152, 190, 101, 0.8);
    }

    span.comments_label>a {
        color: rgba(97, 175, 239, 1);
    }

    a.tag {
        background-color: #2c384e;
        border: none;
        border-radius: 5px;
        color: #bebebe;
    }

    textarea#comment {
        color: #eee;
        background-color: #2c384e;
        font-family: Verdana, Geneva, sans-serif;
        border-top-left-radius: 3px;
        border-top-right-radius: 3px;
        border-bottom-left-radius: 3px;
        border-bottom-right-radius: 3px;
        border-color: transparent;
    }

    .markdown_help_toggler>input,
    .markdown_help_toggler>button {
        color: #eee;
        text-transform: capitalize;
        border-top-left-radius: 3px;
        padding-top: 5px;
        padding-left: 5px;
        padding-right: 5px;
        border-color: transparent;
        background-color: #2c384e;
        font-family: Verdana, Geneva, sans-serif;
        padding-bottom: 5px;
        border-top-right-radius: 3px;
        border-bottom-right-radius: 3px;
        border-bottom-left-radius: 3px;
    }

    span.user_is_author,
    a.user_is_author,
    li .byline a.user_is_author,
    .byline a[class] {
        color: rgba(198, 120, 221, 0.8);
    }

    .comment a {
        color: rgba(97, 175, 239, 1);
    }
  
  	li.story div.details span.link a:visited {
		color: rgb(132, 78, 28);
	}
}
