@-moz-document regexp("^https?\\:\\/\\/www\\.aozora\\.gr\\.jp\\/cards\\/\\d+\\/files\\/[0-9_]+\\.html(#?.+)?") {
/* * base */

html {
	margin: 0;
	padding: 80px 0;
	width: 100%;
	height: 100%;
}
html body {
	margin: 0 calc(100vw / 2 - /*[[BodyWidth]]*/ /2);
	height: /*[[BodyHeight]]*/;
	width: /*[[BodyWidth]]*/;
	-webkit-writing-mode: vertical-rl;
	column-count: 1;
	column-width: auto;
	column-gap: 60px;
	font-size: 1.3em;
	line-height: 1;
}
html, html body, html body > div {
	color: /*[[Color]]*/;
	background-color: /*[[BackgroundColor]]*/;
	line-height: 1.95;
/*[[FontPreset]]*/
}



/* * title and metadata */

h1.title {
	margin: 0;
}
h2 {
	text-indent: -2.2em;
	margin: 6em 0 0 0;
	font-size: larger;
}
h1.title + h2.author {
	margin-right: 0.82em;
}
h2:before {
	font-size: 0.75em;
}
h2.original_title {
	font-size: xx-large;
	font-weight: normal;
	text-indent: 0;
	margin: 0;
	opacity: 0.35;
}
h2.original_title + h2 {
	margin-right: 0.4em;
}
h2.subtitle:before {
	content: "副題　";
}
h2.original_subtitle {
	text-indent: 0;
	opacity: 0.35;
}
h2.author:before {
	content: "　作　";
}
h2.translator {
	text-indent: 0;
}



/* * mokuji */

body > div#contents {
	opacity: 0.6;
}
body > div#contents .contents_visible,
body > div#contents .contents_invisible {
	display: block;
	margin-top: 1.5em;
	margin-left: 0;
}
body > div#contents span {
	display: none;
}
body > div#contents div.contents_visible a[href],
body > div#contents div.contents_invisible a[href] {
	display: inline-block;
	text-indent: 0;
}



/* * text block */

body > div.main_text {
	text-align: justify;
}
body > div.main_text div[style^="margin-left:"],
body > div.main_text div[class^="jisage"] {
	margin-left: 0 !important;
	margin-top: 2em;
	line-height: unset;
}
body > div.main_text div.burasage {
	margin-left: 0 !important;
	margin-top: 2em;
	text-indent: -1em;
}
body > div.main_text span.notes {
	opacity: 0.5;
	font-size: 0.75em;
    /* display: inline-block;/* */
}
body > div.main_text img.gaiji[alt*="小文字"],
body > div.main_text img.gaiji[alt*="大文字"],
body > div.main_text img.gaiji[alt*="括弧"] {
	transform: rotate(90deg);
}
body > div.main_text div[style^="width: "],
body > div.main_text div[class^="jizume"] {
	width: unset !important;
	height: fit-content;
}
body > div.main_text div.keigakomi {
	padding: 0.8em 0.5em;
}
body > div.main_text *[style^="font-size: "][style$="small;"] {
	font-size: 0.85em !important;
}
body > div.main_text *[style^="font-size: "][style$="large;"] {
	font-size: 1.15em !important;
}



/* * elements */

a[href] {
	text-decoration: none;
}
a[href]:hover {
	text-decoration: overline;
}
img {
	max-height: 100%;
	max-width: 100%;
	width: auto;
	height: auto;
}
span.caption {
	font-weight: normal;
	letter-spacing: 0px;
	font-size: unset;
	opacity: 0.5;
}
strong.SESAME_DOT,
em.sesame_dot,
strong.WHITE_CIRCLE,
em.white_circle {
	padding-top: 0;
	display: inline-block;
	background-position: right top;
	background-clip: border-box;
	background-origin: border-box;
	background-size: 0.3em 0.95em;
	background-repeat: repeat-y;
}
strong.SESAME_DOT,
em.sesame_dot {
	background-image: radial-gradient(0.08em 0.08em at center center,/*[[Color]]*/,/*[[Color]]*/ 95%,transparent);
}
strong.WHITE_CIRCLE,
em.white_circle {
	background-image: radial-gradient(0.13em 0.12em at center center,transparent,/*[[Color]]*/ 95%,transparent);
}
strong.SESAME_DOT,
strong.WHITE_CIRCLE {
	font-weight: normal;
}

/* ** ruby */
/* *** rt position bad-hack */
div.main_text ruby {
	display: inline-table;
	text-align-last: center;
	line-height: 1;
}
div.main_text ruby * {
	line-height: 0;
}
div.main_text ruby rt {
	opacity: 0.85;
	margin: 0 -2.45em 0 0;
}
/* *** copy text kakko ins */
div.main_text ruby rp {
	display: inline-block;
	overflow: visible;
	width: 0;
	height: 0;
	line-height: 0;
	font-size: 0px;
}



/* * footer */

body > div.main_text ~ div {
	opacity: 0.5;
	word-break: break-all !important;
	overflow-wrap: break-word !important;
	line-height: 1.35;
	font-size: 0.75em;
}
body > div.main_text ~ div hr + br,
body > div.main_text ~ div br:last-child {
	display: none;
}
body > div.main_text ~ div hr {
	display: none;
}
body::after {
	content: "[END]";
	display: block;
	opacity: 0.25;
	text-align: right;
	width: 100%;
}
/* * eof */
}

