    }
}
@media(max-width:400px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 1 !important;
        --ytd-rich-grid-posts-per-row: 1 !important;
    }
}
ytd-rich-grid-renderer {
    width: 80%;
    margin: auto;
}
ytd-rich-shelf-renderer {
    border-top-width: 1px;
}
#show-more-button.ytd-rich-shelf-renderer {
    border-bottom-width: 1px;
}
#title.ytd-rich-grid-renderer, #title.ytd-rich-shelf-renderer {
    font-size: 1.5rem;
    line-height: 1.5rem;
}
#grid-header.ytd-rich-grid-renderer, #rich-shelf-header.ytd-rich-shelf-renderer {
    margin: 18px 0 18px 18px;
}
#rich-shelf-header.ytd-rich-shelf-renderer {
    margin: 18px 0 18px 0px;
}
#avatar-link {
    display: none;
}
#video-title.ytd-rich-grid-video-renderer {
    font-size: 1.4rem;
    line-height: 1.8rem;
    max-height: 3.5rem;
}
h3.ytd-rich-grid-video-renderer {
    margin: 6px 0 4px 0;
}
#metadata-line.ytd-video-meta-block span.ytd-video-meta-block, #text.complex-string.ytd-channel-name {
    font-size: 1.3rem;
}
}

@-moz-document url-prefix("https://translation.frozendistrict.com") {
:root {
    --body: #2a2a2a;
    --body-lighter: #3F3F3F;
    --font: #eee;
    --font-dark: #3b3b3b;
    --green: #1FA385
}
body, .btn-info, .panel, .zen-unit:nth-child(odd) .translatetext, .multi-wrapper .non-selected-wrapper, .multi-wrapper .selected-wrapper {
    background-color: var(--body);
    color: var(--font);
}
a, footer a, .object-link a, .navbar-inverse .navbar-brand, .navbar-inverse .navbar-nav > li > a, .btn-group-settings a, .ct-label, legend, .full-cell a:hover, a.list-group-item {
    color: var(--font);
}
.breadcrumb a {
    color: var(--font) !important;
}
.navbar, .navbar-default, .navbar-static-top, .navbar-inverse, .btn-primary, .badge {
    background-color: var(--green);
}
.table-striped > tbody > tr:nth-of-type(odd), .btn-default, .zen-unit:nth-child(even), .zen-unit:nth-child(even) .translatetext, .table > tbody > tr > th.warning {
    background-color: var(--body-lighter);
}
.alert-info a {
    color: var(--font-dark);
    text-decoration: underline;
}
.select, .btn-ok, .textinput, .form-control, code, pre, .comment-content, .list-group-item, .btn-link, .search-input {
    background-color: var(--font-dark);
    color: var(--font);
}
.progress {
    background-color: #ADADAD;
}
.btn-link path, .ct-grid {
    fill: var(--font);
    stroke: var(--font);
}
.table > tbody > tr > td {
    border-top: 0;
}
.dropdown-menu, .dropdown-menu > li > a, .clickable-row:hover, .table > tbody > tr.info > th, .current_translation {
    background-color: var(--body-lighter);
    color: var(--font);
}
del {
    background-color: #993232;
}
ins {
    background-color: #428042;
}
}
