@-moz-document domain("minegate.eu"), domain("mmpos.eu") {
.content-block, .files-changed-inner, .commit-stat-summary,
.nav-sidebar,
.td-layout-nav-content, body, html {
    background: #2e2e2e !important;
}

.mat-menu-item,
.mat-select-value span,
.mat-card-subtitle {
    color: #fff !important;
}

.mat-paginator-container {
    border-bottom: 3px solid #3e3e3e !important;
}
.search {
    margin-left: 10px;
}

a {
    color: #fff !important;
    text-decoration: none !important;
}

label, input, span {
    color: #f5f5f5 !important;
}

.bar {
    border-bottom: 0 !important;
}

.mat-list-text,
.mat-header-row, .mat-header-cell, .mat-cell,
.mat-tab-label-content,
.mat-hint {
    color: #fff !important;
}
.files div {
    background: #3e3e3e !important;
}
.tree-item tr:hover,
.idiff { background: rgba(0,0,0,.3) !important; }

.diff-content div table td,
.diff-content div table tr {
	background: rgba(0,0,0,.5) !important;
    color: #fff !important;
    z-index: 0;
}

.diff-content div table td .old,
.diff-content div table tr .old {
    background: rgba(255,0,0,.3) !important;
}

.diff-content div table td .new,
.diff-content div table tr .new {
    background: rgba(0,255,0,.3) !important;
}


.btn-sign-in,
.inline-parallel-buttons a,
.disabled-comment,
.commit-info a,
.header-action-buttons a,
.toggle-sidebar-button,
.dropdown-menu-toggle,
.nav-link,
.info-well,
.mat-dialog-container,
.mat-table,
.mat-paginator,
.mat-toolbar,
.mat-toolbar-single-row {
    background: #3e3e3e !important;
    color: #fff !important;
}

mat-card {
    box-shadow: 0 3px 7px 2px rgba(0,0,0,.3) !important;
    background: #3e3e3e !important;
    color: #fff !important;
    margin-bottom: 15px !important;
}

.mat-toolbar {
    box-shadow: 0 3px 3px 1px rgba(3,169,244,.6) !important;
}


.mat-menu-panel {
    background: #4e4e4e !important;
    border-radius: 15px !important;
}

.mat-option:hover {
    background: rgba(3,169,244,.8) !important;
    transition: 1s background !important;
}
.mat-menu-item:hover {
    background: rgba(3,169,244,.6) !important;
    transition: 1s background !important;
}

.mat-tab-body .mat-card {
    background: #3f3f3f !important;
    box-shadow: 0 1px 3px 0 #000 !important;
    margin-bottom: 8px !important;
}

.mat-raised-button,
.js-view-file,
.js-toggle-diff-comments,
.mat-option,
.mat-dialog-container .mat-card,
.pricing-table .mat-card {
    background: #4e4e4e !important;
}

.gt-sm span:last-child {
    color: #03a9f4 !important;
}

.mat-raised-button.mat-primary,
.mat-button:hover { 
    background: #03a9f4 !important;
    transition: 1s background !important;
}


.mat-tab-header-pagination-chevron {
    border-color: #f5f5f5 !important;
}

.mat-dialog-actions {
    margin-top: 8px;
    padding-top: 0;
    border-top: 2px solid #333;
}

.js-file-title:before {
    background: #2e2e2e !important;
}

.fa-bolt {
    color: yellow;
}

.fa-amd {
    color: red;
}

.fa-nvidia {
    color: green;
}

.pad,
.mat-sidenav-content,
.mat-sidenav {
    color: #fff;
	background: #222 !important;
}

@media (max-width: 768px){
    .td-layout-nav-content{
        padding-top: 15px;
    }
    .td-layout-nav-content .mat-toolbar{
        box-shadow: 0 0 0 0 #fff !important;
        background: #2e2e2e !important;
    }
}
}

