html > body #wp-calendar {
     margin-top: 8px;
     text-align: left;
     width: 165px;
}

#wp-calendar caption {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     border: 1px solid #cad6d9;
     color: #009AFF;
     font-size: 11px;
     margin: 0px;
     padding: 2px 0px 2px 2px;
     text-align: left;
}

#wp-calendar td {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     border-collapse: collapse;
     border: 1px solid #cad6d9;
}

#wp-calendar td a {
     color: #009AFF;
     display: block;
     font-weight: normal;
     width: 100%;
}

#wp-calendar td.pad {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     border: 1px solid #cad6d9;
}

#wp-calendar td#next {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     padding: 0.1em 0px;
}

#wp-calendar td#prev {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     padding: 0.1em 0px;
}

#wp-calendar td#next a, #wp-calendar td#prev a {
     display: inline;
}

#wp-calendar td#next {
     text-align: right;
}

#wp-calendar td#prev {
     text-align: left;
}

