@-moz-document domain("edu.p.lodz.pl") {
:root, html, body {
  --color-text: #f8f8f2;
  --color-text-link: #ffb86c;
  --color-text-link-hover: #ff5555;
  --color-background: #615d61;
  --color-background-logo: #303030;
  --color-background-footer: #3D3937;
  --color-background-logo-invert: #cfcfcf;
  --color-background-logo-grayscale: #303030;
  --color-background-mix-default-logo: #494749; }

:root, html, body {
  --color-text: #f8f8f2;
  --color-text-link: #ffb86c;
  --color-text-link-hover: #ff5555;
  --color-background: #615d61;
  --color-background-logo: #303030;
  --color-background-footer: #3D3937;
  --color-background-logo-invert: #cfcfcf;
  --color-background-logo-grayscale: #303030;
  --color-background-mix-default-logo: #494749; }

#page #page-content #page-navbar {
  box-sizing: border-box;
  border-color: var(--color-background);
  border-radius: 5px;
  padding: 3px; }

#page #page-content #region-main {
  box-sizing: border-box;
  border-color: var(--color-background-logo-invert);
  border-radius: 5px;
  border-style: solid;
  padding: 10px;
  filter: invert(90%); }

#page #page-content #block-region-side-post {
  display: block !important; }
  #page #page-content #block-region-side-post > div > .header > div > div > input {
    display: none; }

#page {
  padding-right: 0px;
  padding-left: 42px;
  padding-left: 0px;
  background-color: var(--color-background); }
  #page #page-header-wrapper {
    background-color: var(--color-background-logo-grayscale); }
    #page #page-header-wrapper > #above-header {
      border-color: var(--color-background-logo); }
    #page #page-header-wrapper > #page-header {
      padding-top: 10px; }
      #page #page-header-wrapper > #page-header > #sitetitle > .sitetitle-images > a:first-child {
        filter: grayscale(100%);
        position: relative;
        top: -20px; }
      #page #page-header-wrapper > #page-header > #sitetitle > .sitetitle-images > a:last-child {
        display: none; }
      #page #page-header-wrapper > #page-header > #sitetitle > .sitetitle-images > a:last-child[title="Strona główna WFTIMS"] {
        display: block;
        filter: invert(81%); }
      #page #page-header-wrapper > #page-header > #sitetitle > .sitetitle-images > a:last-child[title="WIKAMP"] {
        display: block;
        display: block;
        width: 51px;
        height: 65px;
        background-image: url("https://edu.p.lodz.pl/pluginfile.php/1/theme_adaptable/adaptablemarketingimages/0/WIKAMPlogo.png");
        background-repeat: no-repeat;
        background-position-x: center;
        background-position-y: center;
        background-size: 250%; }
        #page #page-header-wrapper > #page-header > #sitetitle > .sitetitle-images > a:last-child[title="WIKAMP"] img {
          display: none; }
      #page #page-header-wrapper > #page-header > #sitetitle > .sitetitle-heading > h1 {
        color: var(--color-text);
        filter: none; }
      #page #page-header-wrapper > #page-header > .socialbox {
        filter: invert(50%); }
  #page #marketblocks > div div {
    border-color: var(--color-background-mix-default-logo);
    border-radius: 5px;
    filter: saturate(0%);
    box-shadow: none; }
  #page #marketblocks > div div:hover {
    filter: initial; }
  #page #page-footer {
    color: var(--color-text);
    background-color: var(--color-background-footer); }
    #page #page-footer .clearfix {
      border-color: var(--color-background-footer); }

#dock {
  background-image: linear-gradient(var(--color-background-logo), var(--color-background-logo), var(--color-background-footer), var(--color-background-footer)); }
}

