    --gray-border-color: #6b6b6b;
    --text-color: #c0c0c0;    
    --text-dim-color: #7f7f7f;    
    --text-dimmer-color: #a0a0a0;
    --text-less-color: #b0b0b0;
    --post-header-color: #5cb7d6;
    --post-timestamp-color: #0dce63;
    --post-rating-color: #88929c;
    --post-3dot-menu-color: #c0c0c0;
    --profile-text-color: #8a9ad4;
    --blue-tab-color: #053d63;
    --blueish-text: #386b7d;
    --blueish-text-medium: #397489;
    --blueish-text-lighter: #4e9cb8;
    --green-mint-text: rgb(17, 197, 97);
    --green-mint-dim-text: rgb(9, 127, 62);
    --text-dblue-bg-color: #1d3a6e;
    --text-orange-color: #b20;
    --dark-transparant-background-color: rgba(16,16,16,.8);
    --selected-nav-color: #18c2fd;
}

/*Page background*/
.reply-tagging-ab-test, .using-click-navigation {
    background-color: var(--dark-bg-color);
}

/*Profile menu*/
.nav-bar-profile-item-name {
    color: var(--text-color);
}
.menu-box, .header-bar-profile-tray {
    background-color: var(--text-darker-bg-color);
}

/*search bar*/
.navbar-left, .header-bar-global-search-input {
    color: var(--text-dim-color);
    background-color: black;
}



/*BEGIN HOME STREAM*/

.nav-bar-list-item-active {
    background-color: var(--green-mint-dim-text);
}

.content-title {
    color: var(--post-header-color);
}

._1evSvvdR {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px var(--text-darker-bg-color) solid;
}
._1IukxMPo {
    color: var(--text-color);
}

.post-container {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px var(--text-darker-bg-color) solid;
}

.comment-detail-author-name {
    color: var(--post-timestamp-color);
}
._1eW-tOzA {
    color: var(--green-mint-dim-text);
}

.css-1c3sz3l {
    color: var(--text-color);
    background-color: var(--text-darker-bg-color);
    border: 1px #000000 solid;
}

/*add a comment...*/
.comment-composer-text-input, .comment-composer-text-input-prompt {
    background: black;
    border: 1px var(--text-darker-bg-color) solid;
}
.comment-composer-action-bar {
    background: var(--text-darker-bg-color);
    border: 1px #000000 solid;
}

/*upper-right neighborhood box*/
.info-box {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px black solid;
}

.css-txitvo {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px #000000 solid;
}
.classified-author-rollup-slider-title {
    color: var(--text-color);
}
.classified-rollup-card-content {
    border: 1px #000000 solid;
}
.classified-rollup-card {
    border: 1px #000000 solid;
}
.slider-card-border {
    border: 1px #000000 solid;
}

.classified-single-item-content-title {
    color: var(--post-header-color);
}

.smartlink-title {
    color: var(--post-header-color);
}

.post-highlight {
    background-color: var(--text-bg-color);
}

/*END HOME STREAM*/



/*BEGIN PROFILE PAGE*/

/*Cards*/
.profile-component-header {
    border-bottom: 1px var(--dark-bg-color) solid;
}
.profile-component-container {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px var(--text-darker-bg-color) solid;
}

/*icons*/
.profile-intro-item-icon {
    filter: brightness(2);
    filter: saturate(8);
    /*filter: invert(100%);*/
}

/*details panel*/
.profile-info-bar-container {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px var(--text-darker-bg-color) solid;
}
.profile-info-bar-header-container {
    border-bottom: 1px var(--dark-bg-color) solid;
}
.info-box-content-container {
    border-top: 1px var(--dark-bg-color) solid;
}
.profile-info-bar-edit-button-right {
    color: var(--blueish-text-lighter);
}

/*Family & Emergency buttons*/
.button-secondary {
    color: var(--text-color);
    background-color: var(--blue-tab-color);
    border-color: var(--blueish-text);
}

.profile-edit-button {
    color: var(--blueish-text-lighter);
}

.tag-button {
    color: var(--text-color);
    border: 1px black solid;
    background-color: var(--blue-tab-color)
}

/*Recent Activity*/
.list, .activity-list {
    color: var(--text-color);
    background-color: var(--text-bg-color);
    border: 1px var(--text-darker-bg-color) solid;
}
.list-title-container {
    border-bottom: 1px var(--dark-bg-color) solid;
}
.list-item {
    border-top: 1px var(--dark-bg-color) solid;
}

/*Left-nav*/
.nav-menu-item-li {
    color: var(--text-color);
}

/*END PROFILE PAGE*/
}

