#nav-menu {
   
    background: #4e616d;     
    margin: auto;
    width: auto;
    padding-top: 37.5px ;
    padding-bottom:37.5px;
    line-height: 15px;
    display: block;
    position: relative;
    min-width: 1200px;
    max-width: 1366px;
    
    z-index: 120;
    }


#nav-menu.align-center>ul {
    text-align: left;
    padding-left: 125px;
    
    
   
}


#nav-menu>ul>li.nav-logo {
    width: 91px;
    position:absolute;
}
#nav-menu>ul>li.nav-logo>a {
    right:530px;
    height:0px;
    padding:0px;
}

#nav-menu>ul>li.nav-logo>a::after,
#nav-menu>ul>li.nav-logo>a:hover::after{
content:'';
width:44px;
height:45px;
margin-left:44px;
background:url("http://ogki71ftx.bkt.clouddn.com//image/logo/LOGO_hover.png")!important;/*url("http://imgur.com/AWp9v7v.png")!important;*/
}

#nav-menu>ul>li.nav-logo>a::before{
content:'keylol'!important;
position:absolute;
z-index:1;
margin-top:10px;
margin-left:18px;
padding-top:5px;    
    
width:75px;

height:20px;
font-weight:bold;
background:#4e616d;
opacity:0;
transition:opacity 0.1s,margin-left 0.2s;
}
#nav-menu>ul>li.nav-logo>a:hover::before{
margin-left:32px;
opacity:1;
}




#nav-menu>ul>li {
    width: 80px;
    float: left;
    margin-right: 0px;
}



#nav-menu>ul>li.has-sub>a{
padding:15px 0px;
text-align:center;
}
#nav-menu>ul>li.has-sub>a:after{
content:'';
}


#ct > div > div.bm.bml.pbn{
display: none;
}




#nav-additional {
height: 0px;
margin: 0px;
padding-left:1080px;
position: relative;
top:-30px;
left: 0px;
}

.tb-container{
position:relative; 
z-index:120;
height:0px;
background:red;
width:1366px;
padding-left:0px;
padding-right:0px;
}

#nav-search-bar,
#nav-search-bar .search-bar{
height:0px;
}




#nav-search-bar{
opacity:1;
margin-right:150px;
margin-top:-47px;
z-index:1;

}




.search-bar >img:first-child{
    display:none;
}


.search-bar .search-bar-form{
position:relative;
right:90px;
margin-left: 0px;
display:block;
width:180px;
background-color: #4e616d;
}







.search-bar .search-box {
   
    border-radius:2px;        
    background-color: #4e616d;
    width:150px;
    height: 26px;
    padding:0px;
    color: #99aab4;
    background:#61717b;
  
    
}




.search-bar .dropdown .btn{
position:relative;
top:-2px;
right:30px;
background: none!important; 
}


.search-bar .dropdown .btn:focus{
outline:none;
}

#nav-search-bar > div > form > div > button > span:nth-child(1){
display:none;
}


.search-bar .search-box:hover {
background:#61717b;
}
.search-bar .search-box:visited {
background:#61717b;
}
.search-bar .search-box:focus {
background:#61717b;
outline:none;/*去除表单项聚焦时焦点时轮廓*/
}




#nav-search-bar > div > form > input.search-box::-webkit-input-placeholder{
color:#99aab4;

text-shadow:none!important;

}

#nav-search-bar > div > form > input.search-box::-moz-placeholder{
color:#99aab4;
text-shadow:none!important;
}


#nav-search-bar > div > form > input.search-box{
opacity:1;
transition:opacity 0.2s;
padding-left:5px;

}


#nav-search-bar:hover  div > form > input.search-box{
opacity:1;
background-color:#61717b;
}


.search-bar button[type=submit]{
display:none;
}

#nav-search-bar > div > form > button.search-button-visible{
box-shadow:none;
border:none;
background:red;
}

.search-bar .dropdown .btn {
    border: none;
    background: #4e616d;
    font-size: 13px;
    padding: 0px 7px;
    color: #99aab4;
}


#nav-user-action-bar.pull-right{
 position:relative;
 top: 0px; /*-52px*/
 /*right: -80px; --- 距离边距 0px*/
 margin-left:80px;
 width:175px;
 z-index:2;
 float:none!important;
background:none;
              
}

#nav-user-action-bar > ul > li:nth-child(1) > a,
#nav-user-action-bar > ul > li:nth-child(2) > a,
#nav-user-action-bar > ul > li:nth-child(3) {
position:relative;
top:-52px;
}

header .btn{
padding:13px 12px;
}



header .img-circle {
    box-shadow: 0px 0px 0px 2px #61717b;
    border-radius: 50%;
    transition:box-shadow 0.2s;
}
header .img-circle:hover{
box-shadow: 0px 0px 0px 5px #61717b;
}


#nav-user-action-bar.pull-right  ul.list-inline  a.btn.btn-user-action{
border-radius:4px;
color: #99aab4;
}
#nav-user-action-bar.pull-right  ul.list-inline  a.btn.btn-user-action:hover{
border-radius:4px;
color: #ffffff;
background-color: #3e4e58;/*#4ed0df*/
}



#nav-user-action-bar .btn-user-action.btn-user-action-highlight{
width:24px;
-webkit-animation: none!important; 
        animation: none!important; 
}



#nav-user-action-bar .btn-user-action.btn-user-action-highlight .badge{
-webkit-animation:badge 1s linear 2s infinite alternate ;    
        animation:badge 1s linear 2s infinite alternate;
position:absolute;
border-radius:2px;
background-color:#fd6c2d;
right:10px;
top:40px;
}

.badge{
padding:3px 8px;
line-height:20px;
}

@keyframes  badge{
    from {color:#fff;}
    to{ color:#ffcdb6;}
}


.badge::before{
    content:'';
    position:absolute;
    width: 0;
    height: 0;
    top:-7px;
    right:7px;
    border-left: 6px solid transparent;
    border-right: 6px solid transparent;
    border-bottom: 8px solid #fd6c2d;

}


#nav-menu>ul>li::after{
border-radius:4px 4px 0px 0px;
}

#nav-menu>ul>li:nth-child(1):hover::after,
#nav-menu>ul>li:nth-child(2):hover::after,
#nav-menu>ul>li:nth-child(3):hover::after,
#nav-menu>ul>li:nth-child(4):hover::after,
#nav-menu>ul>li:nth-child(5):hover::after,
#nav-menu>ul>li:nth-child(6):hover::after,
#nav-menu>ul>li:nth-child(7):hover::after{
background:#3e4e58;/*green*/
}
#nav-menu > ul > li:nth-child(1)>a:hover,
#nav-menu > ul > li:nth-child(2)>a:hover,
#nav-menu > ul > li:nth-child(3)>a:hover,
#nav-menu > ul > li:nth-child(4)>a:hover,
#nav-menu > ul > li:nth-child(5)>a:hover,
#nav-menu > ul > li:nth-child(6)>a:hover,
#nav-menu > ul > li:nth-child(7)>a:hover{
background:#3e4e58;
border-radius:4px 4px 0px 0px;
}


#nav-menu ul li ul li{
border-top:none;
}

#nav-menu > ul > li:nth-child(1) > ul > li > a,
#nav-menu > ul > li:nth-child(2) > ul > li > a,
#nav-menu > ul > li:nth-child(3) > ul > li > a,
#nav-menu > ul > li:nth-child(4) > ul > li > a,
#nav-menu > ul > li:nth-child(5) > ul > li > a,
#nav-menu > ul > li:nth-child(6) > ul > li > a,
#nav-menu > ul > li:nth-child(7) > ul > li > a{
background:#3e4e58;
color:rgba(255,255,255,0.7);
transition: background 0.1s,color 0.1s;
}
#nav-menu > ul > li:nth-child(1) > ul > li > a:hover,
#nav-menu > ul > li:nth-child(2) > ul > li > a:hover,
#nav-menu > ul > li:nth-child(3) > ul > li > a:hover,
#nav-menu > ul > li:nth-child(4) > ul > li > a:hover,
#nav-menu > ul > li:nth-child(5) > ul > li > a:hover,
#nav-menu > ul > li:nth-child(6) > ul > li > a:hover,
#nav-menu > ul > li:nth-child(7) > ul > li > a:hover{
background:#61717b;
color:rgba(255,255,255,1);
}


#nav-menu .dual-line p:last-child {
    text-shadow: 0px 0px!important;
    color: rgba(255,255,255,0.5);
    padding: 0px 6px 0px 6px;
    margin-top: 2px;
    font-size: 12px;
}
}

