@-moz-document url-prefix("https://packagist.org/") {
body,
section.wrapper {
    background-color: #212121;
}

.form-control,
.nav-tabs li a,
#search-form input[type="search"] {
    background: #777777;
    color: #fff;
}

text.nv-legend-text,
.form-control::placeholder,
.form-control::-webkit-input-placeholder {
    color: #fff;
}

#search-container em {
    background: rgba(0, 0, 0, 0.16);
    box-shadow: 0px 0px 2px rgb(179, 78, 34);
}

.search-facets-tags .sbx-sffv__wrapper svg {
    fill: #f28d1a;
}

.search-facets-tags .sbx-sffv__wrapper input::placeholder,
.search-facets-tags .sbx-sffv__wrapper input::-webkit-input-placeholder,
.search-facets-tags .sbx-sffv__wrapper input {
    color: black;
}

g.tick text,
g.nv-axisMaxMin,
g.nv-axisMaxMin-x,
g.nv-axisMin-x,
text.nv-legend-text {
    fill: #fff;
}

.markdown-body .highlight pre,
.markdown-body pre,
pre,
code {
    background-color: black;
    color: gray;
}

.wrapper .content h2 a,
.wrapper .content h2.title,
.wrapper .content h3.title,
.wrapper .container {
    color: #aaaaaa;
}

.wrapper-search a {
    color: #B34E22;
}

.packages .package-item {
    background: #2f2f2f;
    color: #777777;
}

a:hover,
.package .package-aside a:hover {
    color: #f1682c;
}

.package .package-aside {
    background: transparent;
    border: 1px solid #f28d1a;
    border-radius: 5px;
}

.package i,
.package .version-details .version-number {
    color: inherit;
}

.markdown-body .pl-s,
.markdown-body .pl-pds,
.markdown-body .pl-s .pl-pse .pl-s1,
.markdown-body .pl-sr,
.markdown-body .pl-sr .pl-cce,
.markdown-body .pl-sr .pl-sre,
.markdown-body .pl-sr .pl-sra {
    color: #2057ff;
}

.markdown-body .pl-smi,
.markdown-body .pl-s .pl-s1 {
    color: #bdbdbd;
}
}

@charset "UTF-8";

@-moz-document domain(dealabs.com){

/*============================================*/
/*     DarkLabs - MonsieurCro - V20200123     */
/*============================================*/

/* MODS (merci à leipo159, nberdah & Trideal) */
/*============================================*/

/*
[[ mod ]] --> 1px (yes) / 9999px (no)
*/

@media screen and (min-width: /*[[mod]]*/px){

    /*Espacement entre prix barré et prix*/
    .text--lineThrough {
        padding: 1.57143em;
    }
    /*Légère réduction de la taille des images sur l'accueil*/
    .imgFrame-img,
    .ratioBox-child {
        max-width: 90%;
        max-height: 90%;
        margin: auto;
    }
    /*Réduction des espaces des box-deals de l'accueil*/
    .comments-list--insideBox .comments-item-inner,
    .page2-space--h-p, .space--h-3, .space--r-3 {
        padding-right: 1em;
    }
    .space--h-2, .space--r-2 {
        padding-right: 1.57143em;
    }
    .space--fromW3-t-3 {
        padding-top: 1em;
    }
    /*Réduction de la colonne de droite sur l'accueil*/
    .thread-list--type-list {
        max-width: 90%;
        margin: auto;
    }
    /*Décallage du post par apport a l'avatar*/
    .comments-avatarColumn {
        width: 6em;
        box-sizing: content-box;
    }
    /*Quote message*/
    .userHtml-quote {
        padding: .5em 1.57em;
        border: 1px dashed #CCC;
        cursor: default;
        border-radius: 30px;
    }
    /*Grossisement nom user*/
    .user {
        font-weight: 700;
        font-size: larger;
        color: #35373B;
    }
    /*Espacement avatar/message dans les notifications*/
    .notification-item-image {
        width: 5.64286em;
    }
    /*Espacement avatar/message dans la messagerie (partie gauche)*/
    .space--h-1, .space--l-1 {
        padding-left: .28571em;
    }
    /*Encadré deal*/
    .threadGrid.space--t-2.space--b-2.space--h-2.space--fromW3-h-3.space--fromW3-t-3.space--fromW3-b-3 {
        position: relative;
        margin-bottom: 5px;
        border: 1px solid #D1D5DB;
    }
    /*Décalage du quote forum*/
    .userHtml-quote, .userHtml-video, .userHtml-videoDummy, .userHtml[data-animated-gif] {
        display: block;
        margin-top: .28571em;
        margin-bottom: .28571em;
        margin-left: 20px;
    }
    /*Prix barré et remise*/
    .mute--text.size--all-s {
        font-weight: normal;
        font-size: 10pt;
    }
    /*Marchand*/
    .cept-merchant-name {
        font-weight: bold;
        font-size: 10pt;
        font-variant: small-caps;
    }
    /*Description du deal*/
    .cept-description-container {
        color: #DDD;
        font-weight: normal;
        font-size: 10pt;
    }
    /*Barre de navigation - réduit légèrement la barre du haut*/
    .subNav-item {
        height: 3.1em;
    }
    .subNavMenu .subNavMenu-item {
        height: 3.1em; line-height: 3.1em;
    }
    /*Tuiles Deals*/
    .thread-list--type-list .js-threadList .thread--type-list .threadGrid-image .thread-listImgCell {
        width: 10em;
    }
    /*Header onglet "description"*/
    .profileHeader .profileHeader-content,
    .profileHeader > .page-content {
        padding-bottom: .6em;
        padding-top: .6em;
    }
    .grpAvatar {
        width: 4em; min-width: 4em;
    }
    /*Pagination*/
    .ico--type-left-blue:before {
        content: "\2B9C";
        background-image: none;
        color: /*[[blue]]*/;
    }
    .ico--type-right-grey:before {
        content: "\2B9E";
        background-image: none;
        color: #CCC;
    }

}

/*-----#COULEURS-----*/
/*============================================*/

/*
[[ blue ]] --> #1DA1F2
[[ bg ]] --> #141D26
[[ content ]] --> #1B2836
*/

/*Bouton "Back to top"*/
.btn--move-to-top.zIndex--above {
    display: block !important;
    opacity: 1 !important;
    font-size: .8em;
}
.btn--mode-white--dark,
.btn--mode-white--dark:focus,
.btn--mode-white--dark:hover {
    background-color: #121212;
    color: #EEE;
}
/*Bouton sur l'accueil*/
.ico.ico--type-up-brandPrimary.ico--pos-l.hide--toW3:active {
