    background: var(--main-content-color) !important;
    position: relative;
    z-index: 4;
}
.dndupload-progress-inner::after{
    position: absolute;
    
	display: block;
    content: "";
    height: 20px;
    background-color: var(--main-content-color);
    width:100%;
    transform: translateY(-10px); 
    z-index: 2;
        background-color: var(--main-content-color);
    background-image: url("https://1.bp.blogspot.com/-XXAXYwSHQb0/WSQ66A2JiuI/AAAAAAAAAi0/TOWOBq9p1-wcUETDp2tzE_OeqhhoseWuwCLcB/s1600/pacman-eating.gif");
    background-size:contain;
    background-repeat:no-repeat;
    background-position: right;
}
.dndupload-progress-inner::before{
    border-bottom: 3px dotted yellow;
    position: absolute;
	display: block;
    content: "";
    height: 20px;
    width:1000px;
    z-index: 1;
}
